var
	Overlay = require( '../mobile.startup/Overlay' ),
	EditorOverlayBase = require( '../mobile.editor.overlay/EditorOverlayBase' ),
	mfExtend = require( '../mobile.startup/mfExtend' ),
	util = require( '../mobile.startup/util' ),
	CategoryGateway = require( './CategoryGateway' ),
	CategoryLookupInputWidget = require( './CategoryLookupInputWidget' ),
	icons = require( '../mobile.startup/icons' ),
	toast = require( '../mobile.startup/toast' ),
	router = mw.loader.require( 'mediawiki.router' );

/**
 * Displays the list of categories for a page
 * @class CategoryAddOverlay
 * @extends Overlay
 * @uses CategoryGateway
 * @param {Object} options Configuration options
 * @param {OO.EventEmitter} options.eventBus Object used to emit category-added events
 */
function CategoryAddOverlay( options ) {
	options.heading = mw.msg( 'mobile-frontend-categories-add-heading', options.title );
	this.eventBus = options.eventBus;
	Overlay.call(
		this,
		util.extend(
			true,
			{
				className: 'category-overlay overlay',
				events: {
					'click .save': 'onSaveClick',
					'click .suggestion': 'onCategoryClick'
				}
			},
			options
		)
	);
}

mfExtend( CategoryAddOverlay, Overlay, {
	/**
	 * @memberof CategoryAddOverlay
	 * @instance
	 * @mixes Overlay#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {mw.Api} defaults.api to use to construct gateway
	 * @property {string} defaults.waitMsg Text that displays while a page edit is being saved.
	 * @property {string} defaults.waitIcon HTML of the icon that displays while a page edit
	 * is being saved.
	 */
	defaults: util.extend( {}, Overlay.prototype.defaults, {
		waitMsg: mw.msg( 'mobile-frontend-categories-add-wait' ),
		waitIcon: icons.spinner().toHtmlString()
	} ),
	/**
	 * @inheritdoc
	 * @memberof CategoryAddOverlay
	 * @instance
	 */
	template: util.template( `
<div class="overlay-header-container header-container position-fixed">
	{{>header}}
	{{>saveHeader}}
</div>

<div class="content-header panel add-panel">
	<div class="category-add-input"></div>
</div>
<p class="overlay-content category-suggestions panel"></p>
	` ),
	/**
	 * @inheritdoc
	 * @memberof CategoryAddOverlay
	 * @instance
	 */
	templatePartials: util.extend( {}, Overlay.prototype.templatePartials, {
		header: util.template( `
<div class="overlay-header initial-header hideable">
	<ul>
		<li>{{{backButton}}}</li>
	</ul>
	<div class="overlay-title">
		{{{heading}}}
	</div>
	<div class="header-action"><button class="save submit" disabled>{{saveMsg}}</button></div>
</div>
		` ),
		saveHeader: EditorOverlayBase.prototype.templatePartials.saveHeader
	} ),

	/**
	 * @inheritdoc
	 * @memberof CategoryAddOverlay
	 * @instance
	 */
	postRender: function () {
		var input;

		Overlay.prototype.postRender.apply( this );

		this.$suggestions = this.$el.find( '.category-suggestions' );
		this.$saveButton = this.$el.find( '.save' );
		this.wgCategories = this.options.categories;
		this.title = this.options.title;

		this.gateway = new CategoryGateway( this.options.api );
		input = new CategoryLookupInputWidget( {
			gateway: this.gateway,
			suggestions: this.$suggestions,
			categories: this.wgCategories,
			saveButton: this.$saveButton
		} );
		this.$el.find( '.category-add-input' ).append(
			input.$element
		);
	},

	/**
	 * Handle a click on an added category
	 * @memberof CategoryAddOverlay
	 * @instance
	 * @param {jQuery.Event} ev
	 */
	onCategoryClick: function ( ev ) {
		this.$el.find( ev.target ).closest( '.suggestion' ).detach();
		if ( this.$el.find( '.suggestion' ).length > 0 ) {
			this.$saveButton.prop( 'disabled', false );
		} else {
			this.$saveButton.prop( 'disabled', true );
		}
	},

	/**
	 * Handle the click on the save button. Builds a string of new categories
	 * and add it to the article.
	 * @memberof CategoryAddOverlay
	 * @instance
	 */
	onSaveClick: function () {
		var newCategories = '',
			self = this;

		// show the loading spinner and disable the safe button
		this.showHidden( '.saving-header' );

		// add wikitext to add to the page
		this.$el.find( '.suggestion' ).each( function () {
			var data = self.$el.find( this ).data( 'title' );

			if ( data ) {
				// add the new categories in wikitext markup
				newCategories += '\n[[' + data + ']] ';
			}
		} );

		// if there are no categories added, don't do anything (the user shouldn't see the save
		// button)
		if ( newCategories.length === 0 ) {
			toast.show( mw.msg( 'mobile-frontend-categories-nodata' ), { type: 'error' } );
		} else {
			// save the new categories
			this.gateway.save( this.title, newCategories ).then( function () {
				// we're closing the overlay to show the user
				// that the action was completed successful
				router.navigate( '#' );
				mw.notify( mw.msg( 'mobile-frontend-categories-notification' ) );
			}, function () {
				self.showHidden( '.initial-header' );
				self.$safeButton.prop( 'disabled', false );
				// FIXME: Should be a better error message
				toast.show( mw.msg( 'mobile-frontend-categories-nodata' ), { type: 'error' } );
			} );
		}
	}
} );

module.exports = CategoryAddOverlay;
var
	prototype,
	actionParams = require( '../mobile.startup/actionParams' ),
	util = require( '../mobile.startup/util' ),
	SearchGateway = require( '../mobile.startup/search/SearchGateway' );

/**
 * Api for CategoryOverlay
 * @class CategoryGateway
 * @extends SearchGateway
 */
function CategoryGateway() {
	CategoryGateway.parent.apply( this, arguments );
}
prototype = {
	/**
	 * @memberof CategoryGateway
	 * @instance
	 */
	continueParams: {},
	/**
	 * @memberof CategoryGateway
	 * @instance
	 */
	canContinue: true,
	/**
	 * @inheritdoc
	 * @memberof CategoryGateway
	 * @instance
	 */
	searchNamespace: 14,
	/**
	 * Saves the categories passed to this function to the page
	 * @memberof CategoryGateway
	 * @instance
	 * @param {string} title Title of the current page (to add the categories to)
	 * @param {string} categories List of Categories to add
	 * @return {jQuery.Deferred}
	 */
	save: function ( title, categories ) {
		return this.api.postWithToken( 'csrf', {
			action: 'edit',
			title: title,
			appendtext: categories,
			summary: mw.msg( 'mobile-frontend-categories-summary' )
		} );
	},
	/**
	 * Returns the categories the title belongs to.
	 * @memberof CategoryGateway
	 * @instance
	 * @param {string} title Title of the current page (to add the categories to)
	 * @return {jQuery.Deferred|boolean} False, if no further continuation is possible,
	 *                                   jQuery.Deferred otherwise.
	 */
	getCategories: function ( title ) {
		var self = this, params;

		if ( this.canContinue === false ) {
			return false;
		}

		params = util.extend( {}, {
			prop: 'categories',
			titles: title,
			clprop: 'hidden',
			cllimit: 50
		}, this.continueParams );
		return this.api.get( actionParams( params ) ).then( function ( data ) {
			if ( data.continue !== undefined ) {
				self.continueParams = data.continue;
			} else {
				self.canContinue = false;
			}

			return data;
		} );
	}
};

OO.inheritClass( CategoryGateway, SearchGateway );
util.extend( CategoryGateway.prototype, prototype );

module.exports = CategoryGateway;
/**
 * @class CategoryLookupInputWidget
 * @extends OO.ui.mixin.LookupElement
 *
 * @param {Object} options Configuration options
 * @param {CategoryGateway} options.gateway to use to retrieve search results
 * @param {jQuery.Object} options.suggestions container element for search suggestions
 * @param {jQuery.Object} options.saveButton element. Will get disabled when suggested item
 *                                           clicked.
 */
function CategoryLookupInputWidget( options ) {
	// Note: this.$element is set to $( '<div>' ) by OOUI.
	this.gateway = options.gateway;
	this.$suggestions = options.suggestions;
	this.categories = options.categories || [];
	this.$saveButton = options.saveButton;
	options.placeholder = mw.msg( 'mobile-frontend-categories-search' );
	OO.ui.TextInputWidget.call( this, options );
	OO.ui.mixin.LookupElement.call( this, options );
}
OO.inheritClass( CategoryLookupInputWidget, OO.ui.TextInputWidget );
OO.mixinClass( CategoryLookupInputWidget, OO.ui.mixin.LookupElement );

/**
 * Handle a click on a suggested item. Add it to the list of added categories and show save
 * button.
 * @memberof CategoryLookupInputWidget
 * @instance
 * @param {Object} data Data of the clicked element
 */
CategoryLookupInputWidget.prototype.onLookupMenuItemChoose = function ( data ) {
	var button = new OO.ui.ButtonWidget( {
		icon: 'check',
		label: data.label,
		classes: [ 'suggestion', 'suggested' ],
		flags: [ 'progressive', 'primary' ]
	} );

	button.$element.attr( 'data-title', data.data );
	this.$suggestions.append( button.$element );
	this.$saveButton.prop( 'disabled', false );
};

/**
 * Returns the result of the search request.
 * @memberof CategoryLookupInputWidget
 * @instance
 * @return {jQuery.Deferred}
 */
CategoryLookupInputWidget.prototype.getLookupRequest = function () {
	return this.gateway.search( this.value );
};

/**
 * Get lookup cache item from server response data.
 * @memberof CategoryLookupInputWidget
 * @instance
 * @param {Mixed} response Response from server
 * @return {Mixed} Cached result response
 */
CategoryLookupInputWidget.prototype.getLookupCacheDataFromResponse = function ( response ) {
	var title = new mw.Title( this.value, 14 );

	// add user input as a possible (actually not existing) category
	response.results.unshift( {
		title: title.toString(),
		displayTitle: title.getNameText()
	} );

	return response;
};

/**
 * Get a list of menu item widgets from the data stored by the lookup request's done handler.
 * @memberof CategoryLookupInputWidget
 * @instance
 * @param {Mixed} data Cached result data, usually an array
 * @return {Array} Array of OO.ui.MenuOptionWidget
 */
CategoryLookupInputWidget.prototype.getLookupMenuOptionsFromData = function ( data ) {
	var result = [],
		$el = this.$element,
		self = this;

	data.results.forEach( function ( value ) {
		if (
			!$el.find( 'div[data-title="' + value.title + '"]' ).length &&
			self.categories.indexOf( value.displayTitle ) === -1
		) {
			result.push(
				new OO.ui.MenuOptionWidget( {
					data: value.title,
					label: value.displayTitle
				} )
			);
		}
	} );
	return result;
};

module.exports = CategoryLookupInputWidget;
var
	mfExtend = require( '../mobile.startup/mfExtend' ),
	util = require( '../mobile.startup/util' ),
	View = require( '../mobile.startup/View' ),
	ScrollEndEventEmitter = require( '../mobile.startup/ScrollEndEventEmitter' ),
	CategoryGateway = require( './CategoryGateway' );

/**
 * Displays the list of categories for a page in two tabs
 * TODO: Break this into Tab and CategoryList components for better reuse.
 * @class CategoryTabs
 * @extends View
 * @uses CategoryGateway
 *
 * @param {Object} options Configuration options
 * @param {string} options.title of page to obtain categories for
 * @param {string} options.subheading for explaining the list of categories.
 * @param {mw.Api} options.api for use with CategoryGateway
 * @param {OO.EventEmitter} options.eventBus Object used to listen for category-added
 * and scroll:throttled events
 */
function CategoryTabs( options ) {
	this.scrollEndEventEmitter = new ScrollEndEventEmitter( options.eventBus );
	this.scrollEndEventEmitter.on( ScrollEndEventEmitter.EVENT_SCROLL_END,
		this._loadCategories.bind( this ) );
	this.gateway = new CategoryGateway( options.api );
	View.call(
		this,
		util.extend(
			true,
			{ events: { 'click .catlink': 'onCatlinkClick' } },
			options
		)
	);
}

mfExtend( CategoryTabs, View, {
	isTemplateMode: true,
	/**
	 * @memberof CategoryTabs
	 * @instance
	 * @mixes View#defaults
	 * @property {Object} defaults Default options hash.
	 */
	defaults: {
		normalcatlink: mw.msg( 'mobile-frontend-categories-normal' ),
		hiddencatlink: mw.msg( 'mobile-frontend-categories-hidden' )
	},
	/**
	 * @inheritdoc
	 * @memberof CategoryTabs
	 * @instance
	 */
	template: util.template( `
<div class="category-list">
	<p class="content-header">
		{{subheading}}
	</p>
	<ul class="category-header">
		<li class="selected">
			<a href="#" class="catlink">{{normalcatlink}}</a>
		</li><li>
			<a href="#" class="catlink">{{hiddencatlink}}</a>
		</li>
	</ul>
	<ul class="topic-title-list normal-catlist"></ul>
	<ul class="topic-title-list hidden hidden-catlist"></ul>
</div>
{{{spinner}}}
	` ),
	/**
	 * @inheritdoc
	 * @memberof CategoryTabs
	 * @instance
	 */
	templatePartials: {
		item: util.template( `
<li title="{{title}}">
    <a href="{{url}}">{{title}}</a>
</li>
		` )
	},
	/**
	 * @inheritdoc
	 * @memberof CategoryTabs
	 * @instance
	 */
	postRender: function () {
		View.prototype.postRender.apply( this );
		this._loadCategories();
	},

	/**
	 * @memberof CategoryTabs
	 * @instance
	 * @return {void}
	 */
	hideSpinner: function () {
		this.$el.find( '.spinner' ).hide();
	},
	/**
	 * @memberof CategoryTabs
	 * @instance
	 * @return {void}
	 */
	showSpinner: function () {
		this.$el.find( '.spinner' ).show();
	},
	/**
	 * Get a list of categories the page belongs to and re-renders the overlay content
	 * FIXME: CategoryTabs should be dumb and solely focus on rendering. This should
	 * be refactored out at the earliest opportunity.
	 * @memberof CategoryTabs
	 * @instance
	 */
	_loadCategories: function () {
		var self = this,
			$normalCatlist = this.$el.find( '.normal-catlist' ),
			$hiddenCatlist = this.$el.find( '.hidden-catlist' ),
			apiResult;

		this.scrollEndEventEmitter.setElement( this.$el );
		// ScrollEndEventEmitter is enabled once it's created, but we want to wait, until at
		// least one element is in the list before we enable it. So disable it here and enable
		// once the elements are loaded.
		this.scrollEndEventEmitter.disable();
		apiResult = this.gateway.getCategories( this.options.title );
		if ( apiResult === false ) {
			self.hideSpinner();
			return;
		}
		apiResult.then( function ( data ) {
			if ( data.query && data.query.pages ) {
				// add categories to overlay
				data.query.pages.forEach( function ( page ) {
					if ( page.categories ) {
						page.categories.forEach( function ( category ) {
							var title = mw.Title.newFromText( category.title, category.ns );

							if ( category.hidden ) {
								$hiddenCatlist.append( self.templatePartials.item.render( {
									url: title.getUrl(),
									title: title.getNameText()
								} ) );
							} else {
								$normalCatlist.append( self.templatePartials.item.render( {
									url: title.getUrl(),
									title: title.getNameText()
								} ) );
							}
						} );
					}
				} );

				if ( $normalCatlist.length === 0 && $normalCatlist.length === 0 ) {
					self.$el.find( '.content-header' ).text( mw.msg( 'mobile-frontend-categories-nocat' ) );
				} else if ( $normalCatlist.length === 0 && $normalCatlist.length > 0 ) {
					this._changeView();
				}
			} else {
				self.$el.find( '.content-header' ).text( mw.msg( 'mobile-frontend-categories-nocat' ) );
			}
			self.hideSpinner();
			self.scrollEndEventEmitter.enable();
		} );
	},

	/**
	 * Handles a click on one of the tabs to change the viewable categories
	 * @memberof CategoryTabs
	 * @instance
	 * @param {jQuery.Event} ev The Event object triggered this handler
	 */
	onCatlinkClick: function ( ev ) {
		ev.preventDefault();
		// change view only, if the user clicked another view
		if ( !this.$el.find( ev.target ).parent().hasClass( 'selected' ) ) {
			this._changeView();
		}
	},

	/**
	 * Changes the view from hidden categories to content-based categories and vice-versa
	 * @memberof CategoryTabs
	 * @instance
	 */
	_changeView: function () {
		this.$el.find( '.category-header li' ).toggleClass( 'selected' );
		this.$el.find( '.topic-title-list' ).toggleClass( 'hidden' );
	}
} );

module.exports = CategoryTabs;
var
	Overlay = require( '../mobile.startup/Overlay' ),
	CategoryTabs = require( './CategoryTabs' );

/**
 * Gets an overlay for displaying categories
 *
 * @param {Object} options Configuration options
 * @param {string} options.title of page to obtain categories for
 * @param {mw.Api} options.api for usage in CategoryTabs
 * @param {string} options.subheading for usage in CategoryTabs
 * @param {OO.EventEmitter} options.eventBus Object used to listen for category-added
 * and scroll:throttled events
 * @return {Overlay}
 */
function categoryOverlay( options ) {
	var overlay, widget,
		headerButtons = options.isAnon ? [] :
			[ {
				href: '#/categories/add',
				className: 'add continue',
				msg: mw.msg( 'mobile-frontend-categories-add' )
			} ];
	overlay = new Overlay( {
		className: 'category-overlay overlay',
		heading: mw.msg( 'mobile-frontend-categories-heading' ),
		headerButtons: headerButtons
	} );
	widget = new CategoryTabs(
		{
			eventBus: options.eventBus,
			api: options.api,
			title: options.title,
			subheading: mw.msg( 'mobile-frontend-categories-subheading' )
		}
	);
	overlay.$el.find( '.overlay-content' ).append( widget.$el );
	return overlay;
}

module.exports = categoryOverlay;
var m = require( '../mobile.startup/moduleLoaderSingleton' ),
	CategoryAddOverlay = require( './CategoryAddOverlay' ),
	categoryOverlay = require( './categoryOverlay' );

// needed for minerva usages
m.define( 'mobile.categories.overlays/CategoryAddOverlay', CategoryAddOverlay );
m.define( 'mobile.categories.overlays/categoryOverlay', categoryOverlay );
