var
	View = require( './View' ),
	mfExtend = require( './mfExtend' );

/**
 * A wrapper for creating an anchor.
 * @class Anchor
 * @extends View
 */
function Anchor() {
	View.apply( this, arguments );
}

mfExtend( Anchor, View, {
	/**
	 * @inheritdoc
	 * @memberof Anchor
	 * @instance
	 */
	isTemplateMode: true,
	/**
	 * @memberof Anchor
	 * @instance
	 * @mixes View#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {boolean} defaults.progressive is progressive action
	 * @property {boolean} defaults.destructive is destructive action
	 * @property {string} defaults.additionalClassNames Additional class name(s).
	 * @property {string} defaults.href url
	 * @property {string} defaults.label of anchor
	 */
	defaults: {
		progressive: undefined,
		destructive: undefined,
		additionalClassNames: '',
		href: undefined,
		label: undefined
	},
	/**
	 * @inheritdoc
	 * @memberof Anchor
	 * @instance
	 */
	template: mw.template.get( 'mobile.startup', 'anchor.hogan' )
} );

module.exports = Anchor;
var
	util = require( './util' ),
	browser;

/**
 * Memoize a class method. Caches the result of the method based on the
 * arguments. Instances do not share a cache.
 * @param {Function} method Method to be memoized
 * @return {Function}
 */
function memoize( method ) {
	/**
	 * Memoized version of the method
	 * @return {Function}
	 */
	var memoized = function () {
		var cache = this[ '__cache' + memoized.cacheId ] ||
			( this[ '__cache' + memoized.cacheId ] = {} ),
			key = [].join.call( arguments, '|' );
		if ( Object.prototype.hasOwnProperty.call( cache, key ) ) {
			return cache[ key ];
		}
		return ( cache[ key ] = method.apply( this, arguments ) );
	};
	memoized.cacheId = Date.now().toString() + Math.random().toString();
	return memoized;
}

/**
 * Representation of user's current browser
 * @class Browser
 * @param {string} ua the user agent of the current browser
 * @param {jQuery.Object} $container an element to associate with the Browser object
 */
function Browser( ua, $container ) {
	this.userAgent = ua;
	this.$el = $container;
	this._fixIosLandscapeBug();
}

Browser.prototype = {
	/**
	 * When rotating to landscape stop page zooming on ios 4 and 5.
	 * @memberof Browser
	 * @instance
	 * @private
	 */
	_fixIosLandscapeBug: function () {
		var self = this,
			viewport = this.$el.find( 'meta[name="viewport"]' )[0];

		// see http://adactio.com/journal/4470/ (fixed in ios 6)
		if ( viewport && ( this.isIos( 4 ) || this.isIos( 5 ) ) ) {
			this.lockViewport();
			document.addEventListener( 'gesturestart', function () {
				self.lockViewport();
			}, false );
		}
	},
	/**
	 * Returns whether the current browser is an ios device.
	 * FIXME: jquery.client does not support iPad detection so we cannot use it.
	 * @memberof Browser
	 * @instance
	 * @param {number} [version] integer describing a specific version you want to test against.
	 * @return {boolean}
	 */
	isIos: memoize( function ( version ) {
		var ua = this.userAgent,
			ios = /ipad|iphone|ipod/i.test( ua );

		if ( ios && version ) {
			switch ( version ) {
				case 8:
					// Test UA for iOS8. Or for simulator look for Version 8
					// In the iOS simulator the OS is the host machine OS version
					// This makes testing in iOS8 simulator work as expected
					return /OS 8_/.test( ua ) || /Version\/8/.test( ua );
				case 4:
					return /OS 4_/.test( ua );
				case 5:
					return /OS 5_/.test( ua );
				default:
					return false;
			}
		} else {
			return ios;
		}
	} ),
	/**
	 * Locks the viewport so that pinch zooming is disabled
	 * @memberof Browser
	 * @instance
	 */
	lockViewport: function () {
		if ( this.$el ) {
			this.$el.find( 'meta[name="viewport"]' )
				.attr( 'content', 'initial-scale=1.0, maximum-scale=1.0, user-scalable=no' );
		}
	},
	/**
	 * Determine if a device has a widescreen.
	 * @memberof Browser
	 * @instance
	 * @return {boolean}
	 */
	isWideScreen: memoize( function () {
		var val = parseInt( mw.config.get( 'wgMFDeviceWidthTablet' ), 10 );
		// Check portrait and landscape mode to be consistent
		return window.innerWidth >= val || window.innerHeight >= val;
	} ),
	/**
	 * Checks browser support for CSS transforms, transitions
	 * and CSS animation.
	 * Currently assumes support for the latter 2 in the case of the
	 * former.
	 * See http://stackoverflow.com/a/12621264/365238
	 * @memberof Browser
	 * @instance
	 * @return {boolean}
	 */
	supportsAnimations: memoize( function () {
		var elemStyle = document.createElement( 'foo' ).style;
		function supportsProperty( property ) {
			// We only test "webkit-", because that's the only prefix needed for the relevant
			// properties (in supportsAnimations) and supported browsers. If usage is expanded,
			// other prefixes may need to be checked as well.
			return property in elemStyle ||
				( 'webkit' + property[ 0 ].toUpperCase() + property.slice( 1 ) ) in elemStyle;
		}
		return supportsProperty( 'animationName' ) &&
			supportsProperty( 'transform' ) &&
			supportsProperty( 'transition' );
	} ),
	/**
	 * Whether touchstart and other touch events are supported by the current browser.
	 * @memberof Browser
	 * @instance
	 * @return {boolean}
	 */
	supportsTouchEvents: memoize( function () {
		return 'ontouchstart' in window;
	} ),
	/**
	 * Detect if browser supports geolocation
	 * @memberof Browser
	 * @instance
	 * @return {boolean}
	 */
	supportsGeoLocation: memoize( function () {
		return 'geolocation' in window.navigator;
	} )
};

/**
 * @memberof Browser
 * @return {Browser}
 */
Browser.getSingleton = function () {
	var $html;
	if ( !browser ) {
		$html = util.getDocument();
		browser = new Browser( window.navigator.userAgent, $html );
	}
	return browser;
};

module.exports = Browser;
var
	mfExtend = require( './mfExtend' ),
	View = require( './View' );

/**
 * A wrapper for creating a button.
 * @class Button
 * @extends View
 *
 * @param {Object} options Configuration options
 */
function Button( options ) {
	if ( options.href ) {
		options.tagName = 'a';
	}
	View.call( this, options );
}

mfExtend( Button, View, {
	/**
	 * @inheritdoc
	 * @memberof Button
	 * @instance
	 */
	isTemplateMode: true,
	/**
	 * @memberof Button
	 * @instance
	 * @mixes View#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {string} defaults.tagName The name of the tag in which the button is wrapped.
	 * @property {boolean} defaults.block is stacked button
	 * @property {boolean} defaults.progressive is progressive action
	 *   This option is deprecated. Please use `progressive`.
	 * @property {boolean} defaults.quiet is quiet button
	 * @property {boolean} defaults.destructive is destructive action
	 * @property {string} defaults.additionalClassNames Additional class name(s).
	 * @property {string} defaults.href url
	 * @property {string} defaults.label of button
	 */
	defaults: {
		tagName: 'a',
		block: undefined,
		progressive: undefined,
		destructive: undefined,
		quiet: undefined,
		additionalClassNames: '',
		href: undefined,
		label: undefined
	},
	/**
	 * @memberof Button
	 * @instance
	 */
	template: mw.template.get( 'mobile.startup', 'button.hogan' )
} );

module.exports = Button;
var
	mfExtend = require( './mfExtend' ),
	Drawer = require( './Drawer' ),
	util = require( './util' ),
	Button = require( './Button' ),
	Anchor = require( './Anchor' );

/**
 * @typedef {string|number|boolean|undefined} QueryVal
 * @typedef {Object.<string, QueryVal|QueryVal[]>} QueryParams
 *
 * @typedef {Object} Options
 * @prop {string} [returnTo]
 * @prop {QueryParams} [queryParams]
 * @prop {QueryParams} [signupQueryParams]
 * @prop {Object} [options.progressiveButton] template options for Button element for signing in
 * @prop {Object} [options.actionAnchor] template options for Anchor element for signing up
 */

/**
 * This creates the drawer at the bottom of the screen that appears when an anonymous
 * user tries to perform an action that requires being logged in. It presents the user
 * with options to log in or sign up for a new account.
 * @class CtaDrawer
 * @extends Drawer
 * @uses Button
 * @uses Icon
 * @uses Anchor
 * @param {Options} options
 */
function CtaDrawer( options ) {
	Drawer.call( this,
		util.extend( {}, Drawer.prototype.defaults, {
			progressiveButton: new Button( {
				progressive: true,
				label: mw.msg( 'mobile-frontend-watchlist-cta-button-login' )
			} ).options,
			actionAnchor: new Anchor( {
				progressive: true,
				label: mw.msg( 'mobile-frontend-watchlist-cta-button-signup' )
			} ).options
		}, options )
	);
}

mfExtend( CtaDrawer, Drawer, {
	/**
	 * @memberof CtaDrawer
	 * @instance
	 */
	templatePartials: util.extend( {}, Drawer.prototype.templatePartials, {
		button: Button.prototype.template,
		anchor: Anchor.prototype.template
	} ),
	/**
		 * @memberof CtaDrawer
		 * @instance
		 */
	template: mw.template.get( 'mobile.startup', 'CtaDrawer.hogan' ),
	/**
		 * @inheritdoc
		 * @memberof CtaDrawer
		 * @instance
		 */
	preRender: function () {
		var params = redirectParams( this.options.queryParams, this.options.returnTo );

		// Give the button and the anchor a default target, if it isn't set already. Buttons are
		// customized by Minerva's red link drawer, skins.minerva.scripts/init.js.
		if ( !this.options.progressiveButton.href ) {
			this.options.progressiveButton.href = mw.util.getUrl( 'Special:UserLogin', params );
		}
		if ( !this.options.actionAnchor.href ) {
			this.options.actionAnchor.href = mw.util.getUrl(
				'Special:UserLogin', signUpParams( params, this.options.signupQueryParams )
			);
		}
	}
} );

/**
 * Special:UserLogin post-request redirect query parameters.
 * @param {QueryParams} params
 * @param {string} [redirectURL]
 * @return {QueryParams}
 */
function redirectParams( params, redirectURL ) {
	return util.extend( {
		// use wgPageName as this includes the namespace if outside Main
		returnto: redirectURL || mw.config.get( 'wgPageName' )
	}, params );
}

/**
 * Special:UserLogin account creation query parameters.
 * @param {...QueryParams} params
 * @return {QueryParams}
 */
function signUpParams() {
	[].push.call( arguments, { type: 'signup' } );
	return util.extend.apply( util, arguments );
}

CtaDrawer.prototype.test = {
	redirectParams: redirectParams,
	signUpParams: signUpParams
};

module.exports = CtaDrawer;
var
	mfExtend = require( './mfExtend' ),
	Panel = require( './Panel' ),
	util = require( './util' ),
	Icon = require( './Icon' );

/**
 * A {@link View} that pops up from the bottom of the screen.
 * @class Drawer
 * @extends Panel
 * @param {Object} props
 */
function Drawer( props ) {
	Panel.call( this,
		util.extend( {
			className: 'drawer position-fixed'
		}, props )
	);
}

mfExtend( Drawer, Panel, {
	/**
	 * @memberof Drawer
	 * @instance
	 * @mixes Panel#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {string} defaults.cancelButton HTML of the button that closes the drawer.
	 */
	defaults: util.extend( {}, Panel.prototype.defaults, {
		// Used by CtaDrawer, BlockMessage.
		collapseIcon: new Icon( {
			name: 'arrow',
			additionalClassNames: 'cancel'
		} ).options
	} ),
	/**
	 * @memberof Drawer
	 * @instance
	 */
	templatePartials: util.extend( {}, Panel.prototype.templatePartials, {
		icon: Icon.prototype.template
	} ),
	/**
	 * Defines an element that the Drawer should automatically be appended to.
	 * @memberof Drawer
	 * @instance
	 * @property {string}
	 */
	appendToElement: 'body',
	/**
	 * Whether the drawer should disappear on a scroll event
	 * @memberof Drawer
	 * @instance
	 * @property {boolean}
	 */
	closeOnScroll: true,
	events: util.extend( {}, Panel.prototype.events, {
		click: 'stopPropagation'
	} ),

	/**
	 * @inheritdoc
	 * @memberof Drawer
	 * @instance
	 */
	postRender: function () {
		var self = this;
		// This module might be loaded at the top of the page e.g. Special:Uploads
		// Thus ensure we wait for the DOM to be loaded
		util.docReady( function () {
			self.appendTo( self.appendToElement );
			self.$el.parent().addClass( 'has-drawer' );
		} );
		this.on( 'show', this.onShowDrawer.bind( this ) );
		this.on( 'hide', this.onHideDrawer.bind( this ) );
	},
	/**
	 * Stop Propagation event handler
	 * @memberof Drawer
	 * @instance
	 * @param {Object} ev event object
	 * Allow the drawer itself to be clickable (e.g. for copying and pasting references
	 * clicking links in reference)
	 */
	stopPropagation: function ( ev ) {
		ev.stopPropagation();
	},

	/**
	 * ShowDrawer event handler
	 * @memberof Drawer
	 * @instance
	 */
	onShowDrawer: function () {
		var self = this;

		this.$el.parent().addClass( 'drawer-visible' );

		setTimeout( function () {
			var $window = util.getWindow();
			$window.one( 'click.drawer', self.hide.bind( self ) );
			if ( self.closeOnScroll ) {
				$window.one( 'scroll.drawer', self.hide.bind( self ) );
			}
		}, self.minHideDelay );
	},

	/**
	 * HideDrawer event handler
	 * @memberof Drawer
	 * @instance
	 */
	onHideDrawer: function () {
		this.$el.parent().removeClass( 'drawer-visible' );
		// .one() registers one callback for scroll and click independently
		// if one fired, get rid of the other one
		util.getWindow().off( '.drawer' );
	}
} );

module.exports = Drawer;
var
	mfExtend = require( './mfExtend' ),
	View = require( './View' );

/**
 * A wrapper for creating an icon.
 * @class Icon
 * @extends View
 *
 * @param {Object} options Configuration options
 */
function Icon( options ) {
	if ( options.hasText ) {
		options.modifier = 'mw-ui-icon-before';
	}
	if ( options.href ) {
		options.tagName = 'a';
	}
	View.call( this, options );
}

mfExtend( Icon, View, {
	/**
	 * @inheritdoc
	 * @memberof Icon
	 * @instance
	 */
	preRender: function () {
		this.setRotationClass();
	},
	/**
	 * Internal method that sets the correct rotation class for the icon
	 * based on the value of rotation
	 * @memberof Icon
	 * @instance
	 * @private
	 */
	setRotationClass: function () {
		var options = this.options;
		if ( options.rotation ) {
			switch ( options.rotation ) {
				case -180:
				case 180:
					options._rotationClass = 'mf-mw-ui-icon-rotate-flip';
					break;
				case -90:
					options._rotationClass = 'mf-mw-ui-icon-rotate-anti-clockwise';
					break;
				case 90:
					options._rotationClass = 'mf-mw-ui-icon-rotate-clockwise';
					break;
				case 0:
					break;
				default:
					throw new Error( 'Bad value for rotation given. Must be ±90, 0 or ±180.' );
			}
		}
	},
	/**
	 * @inheritdoc
	 * @memberof Icon
	 * @instance
	 */
	isTemplateMode: true,
	/**
	 * @memberof Icon
	 * @instance
	 * @mixes View#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {boolean} defaults.hasText Whether the icon has text.
	 * @property {boolean} defaults.isSmall Whether the icon should be small.
	 * @property {string} [defaults.href] value of href attribute,
	 *  when set tagName will default to anchor tag
	 * @property {string} defaults.tagName The name of the tag in which the icon is wrapped.
	 *  Defaults to 'a' when href option present.
	 * @property {string} defaults.base String used as a base for generating class names.
	 * Defaults to 'mw-ui-icon'.
	 * @property {string} defaults.name Name of the icon.
	 * @property {string} defaults.modifier Additional class name.
	 * Defaults to 'mw-ui-icon-element'.
	 * @property {string} defaults.title Tooltip text.
	 * @property {boolean} defaults.rotation will rotate the icon by a certain number
	 *  of degrees.
	 *  Must be ±90, 0 or ±180 or will throw exception.
	 */
	defaults: {
		rotation: 0,
		hasText: false,
		href: undefined,
		glyphPrefix: 'mf',
		tagName: 'div',
		isSmall: false,
		base: 'mw-ui-icon',
		name: '',
		modifier: 'mw-ui-icon-element',
		title: ''
	},
	/**
	 * Return the full class name that is required for the icon to render
	 * @memberof Icon
	 * @instance
	 * @return {string}
	 */
	getClassName: function () {
		return this.$el.attr( 'class' );
	},
	/**
	 * Return the class that relates to the icon glyph
	 * @memberof Icon
	 * @instance
	 * @return {string}
	 */
	getGlyphClassName: function () {
		return this.options.base + '-' + this.options.glyphPrefix + '-' + this.options.name;
	},
	/**
	 * Return the HTML representation of this view
	 * @memberof Icon
	 * @instance
	 * @return {string}
	 */
	toHtmlString: function () {
		return this.parseHTML( '<div>' ).append( this.$el ).html();
	},
	template: mw.template.get( 'mobile.startup', 'icon.hogan' )
} );

module.exports = Icon;
var
	View = require( './View' ),
	mfExtend = require( './mfExtend' );

/**
 * @class MessageBox
 * @extends View
 */
function MessageBox() {
	View.apply( this, arguments );
}

mfExtend( MessageBox, View, {
	/**
	 * @inheritdoc
	 * @memberof MessageBox
	 * @instance
	 */
	isTemplateMode: true,
	/**
	 * @memberof MessageBox
	 * @instance
	 * @mixes View#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {string} [defaults.heading] heading to show along with message (text)
	 * @property {string} defaults.msg message to show (html)
	 * @property {string} defaults.className either errorbox, warningbox or successbox
	 */
	defaults: {},
	/**
	 * @memberof MessageBox
	 * @instance
	 */
	template: mw.template.get( 'mobile.startup', 'MessageBox.hogan' )
} );

module.exports = MessageBox;
var
	View = require( './View' ),
	Icon = require( './Icon' ),
	Button = require( './Button' ),
	Anchor = require( './Anchor' ),
	icons = require( './icons' ),
	util = require( './util' ),
	browser = require( './Browser' ).getSingleton(),
	mfExtend = require( './mfExtend' );

/**
 * Mobile modal window
 * @class Overlay
 * @extends View
 * @uses Icon
 * @uses Button
 * @fires Overlay#Overlay-exit
 * @fires Overlay#hide
 * @param {Object} props
 * @param {boolean} props.noHeader renders an overlay without a header
 */
function Overlay( props ) {
	this.isIos = browser.isIos();
	this.useVirtualKeyboardHack = browser.isIos( 4 ) || browser.isIos( 5 );
	// Set to true when overlay has failed to load
	this.hasLoadError = false;
	View.call( this, util.extend( {
		className: 'overlay'
	}, props ) );
}

mfExtend( Overlay, View, {
	/**
	 * Identify whether the element contains position fixed elements
	 * @memberof Overlay
	 * @instance
	 * @property {boolean}
	 */
	hasFixedHeader: true,
	/**
	 * Is overlay fullscreen
	 * @memberof Overlay
	 * @instance
	 * @property {boolean}
	 */
	fullScreen: true,

	/**
	 * True if this.hide() should be invoked before firing the Overlay-exit
	 * event
	 * @memberof Overlay
	 * @instance
	 * @property {boolean}
	 */
	hideOnExitClick: true,

	templatePartials: {
		header: mw.template.get( 'mobile.startup', 'header.hogan' ),
		anchor: Anchor.prototype.template,
		button: Button.prototype.template
	},
	template: mw.template.get( 'mobile.startup', 'Overlay.hogan' ),
	/**
	 * @memberof Overlay
	 * @instance
	 * @mixes View#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {string} defaults.saveMessage Caption for save button on edit form.
	 * @property {string} defaults.cancelButton HTML of the cancel button.
	 * @property {string} defaults.backButton HTML of the back button.
	 * @property {string} defaults.headerButtonsListClassName A comma separated string of class
	 * names of the wrapper of the header buttons.
	 * @property {boolean} defaults.headerChrome Whether the header has chrome.
	 * @property {string} defaults.spinner HTML of the spinner icon.
	 * @property {Object} [defaults.footerAnchor] options for an optional Anchor
	 *  that can appear in the footer
	 */
	defaults: {
		saveMsg: mw.msg( 'mobile-frontend-editor-save' ),
		cancelButton: icons.cancel().toHtmlString(),
		backButton: new Icon( {
			tagName: 'button',
			name: 'back',
			additionalClassNames: 'back',
			label: mw.msg( 'mobile-frontend-overlay-close' )
		} ).toHtmlString(),
		headerButtonsListClassName: '',
		headerChrome: false,
		spinner: icons.spinner().toHtmlString()
	},
	/**
	 * @memberof Overlay
	 * @instance
	 */
	events: {
		// FIXME: Remove .initial-header selector when bug 71203 resolved.
		'click .cancel, .confirm, .initial-header .back': 'onExitClick',
		click: 'stopPropagation'
	},
	/**
	 * Flag overlay to close on content tap
	 * @memberof Overlay
	 * @instance
	 * @property {boolean}
	 */
	closeOnContentTap: false,

	/**
	 * Shows the spinner right to the input field.
	 * @memberof Overlay
	 * @instance
	 * @method
	 */
	showSpinner: function () {
		this.$spinner.removeClass( 'hidden' );
	},

	/**
	 * Hide the spinner near to the input field.
	 * @memberof Overlay
	 * @instance
	 * @method
	 */
	hideSpinner: function () {
		this.$spinner.addClass( 'hidden' );
	},

	/**
	 * @inheritdoc
	 * @memberof Overlay
	 * @instance
	 */
	postRender: function () {

		this.$overlayContent = this.$( '.overlay-content' );
		this.$spinner = this.$( '.spinner' );
		if ( this.isIos ) {
			this.$el.addClass( 'overlay-ios' );
		}
		// Truncate any text inside in the overlay header.
		this.$( '.overlay-header h2 span' ).addClass( 'truncated-text' );
		this.setupEmulatedIosOverlayScrolling();
	},

	/**
	 * Setups an emulated scroll behaviour for overlays in ios.
	 * @memberof Overlay
	 * @instance
	 */
	setupEmulatedIosOverlayScrolling: function () {
		var self = this;
		if ( this.isIos && this.hasFixedHeader ) {
			this.$( '.overlay-content' ).on( 'touchstart', this.onTouchStart.bind( this ) )
				.on( 'touchmove', this.onTouchMove.bind( this ) );
			// wait for things to render before doing any calculations
			setTimeout( function () {
				self._fixIosHeader( self.$( 'textarea, input' ) );
			}, 0 );
		}
	},
	/**
	 * ClickBack event handler
	 * @memberof Overlay
	 * @instance
	 * @param {Object} ev event object
	 */
	onExitClick: function ( ev ) {
		ev.preventDefault();
		ev.stopPropagation();
		if ( this.hideOnExitClick ) {
			this.hide();
		}
		this.emit( Overlay.EVENT_EXIT );
	},
	/**
	 * Event handler for touchstart, for IOS
	 * @memberof Overlay
	 * @instance
	 * @param {Object} ev Event Object
	 */
	onTouchStart: function ( ev ) {
		this.startY = ev.originalEvent.touches[0].pageY;
	},
	/**
	 * Event handler for touch move, for IOS
	 * @memberof Overlay
