	if ( !mw.user.isAnon() ) {
		new mw.Api().saveOption( 'mobile-editor', editor ).then( function () {
			mw.user.options.set( 'mobile-editor', editor );
		} );
	} else {
		mw.storage.set( 'preferredEditor', editor );
	}
