var Button = require( '../mobile.startup/Button' ),
	util = require( '../mobile.startup/util' ),
	mfExtend = require( '../mobile.startup/mfExtend' ),
	Overlay = require( '../mobile.startup/Overlay' );

/**
 * Overlay that shows a message about abuse.
 * This overlay is rendered when the error code from the API
 * is related to the abusefilter extension.
 * @class AbuseFilterOverlay
 * @extends Overlay
 * @param {Object} props
 */
function AbuseFilterOverlay( props ) {
	Overlay.call( this,
		util.extend( {
			className: 'overlay abusefilter-overlay'
		}, props )
	);
}

mfExtend( AbuseFilterOverlay, Overlay, {
	/**
	 * @memberof AbuseFilterOverlay
	 * @instance
	 * @mixes Overlay#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {Object} defaults.confirmButton options for a confirm Button
	 */
	defaults: util.extend( {}, Overlay.prototype.defaults, {
		confirmButton: new Button( {
			additionalClassNames: 'cancel',
			progressive: true
		} ).options
	} ),
	/**
	 * @memberof AbuseFilterOverlay
	 * @instance
	 */
	templatePartials: util.extend( {}, Overlay.prototype.templatePartials, {
		button: Button.prototype.template,
		content: util.template( `
<div class="content">
	{{{message}}}
	{{#confirmButton}}{{>button}}{{/confirmButton}}
</div>
		` )
	} ),
	/**
	 * @inheritdoc
	 * @memberof AbuseFilterOverlay
	 * @instance
	 */
	postRender: function () {
		Overlay.prototype.postRender.apply( this );
		// make links open in separate tabs
		this.$el.find( 'a' ).attr( 'target', '_blank' );
	}
} );

module.exports = AbuseFilterOverlay;
var util = require( '../mobile.startup/util' ),
	View = require( '../mobile.startup/View' ),
	mfExtend = require( '../mobile.startup/mfExtend' ),
	AbuseFilterOverlay = require( './AbuseFilterOverlay' );

/**
 * Panel that shows an error message related to the abusefilter extension.
 * @class AbuseFilterPanel
 * @extends View
 * @uses AbuseFilterOverlay
 *
 * @param {Object} options Configuration options
 * FIXME: should extend Panel not View. Or the name should be changed to something meaningful.
 */
function AbuseFilterPanel( options ) {
	this.isDisallowed = false;
	this.overlayManager = options.overlayManager;
	View.call( this,
		util.extend( {
			className: 'panel hidden'
		}, options )
	);
}

mfExtend( AbuseFilterPanel, View, {
	/**
	 * @memberof AbuseFilterPanel
	 * @instance
	 * @mixes View#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {string} defaults.readMoreMsg A caption for the button
	 * allowing the user to read more about the problems with their edit.
	 * @property {OverlayManager} defaults.overlayManager instance
	 */
	defaults: {
		readMoreMsg: mw.msg( 'mobile-frontend-editor-abusefilter-read-more' )
	},
	/**
	 * @memberof AbuseFilterPanel
	 * @instance
	 */
	template: util.template( `
<div class="message">
	<p></p><a href="#/abusefilter" class="readmore">{{readMoreMsg}}</a>
</div>
	` ),
	/**
	 * Show the panel. Create a route to show AbuseFilterOverlay with message.
	 * @memberof AbuseFilterPanel
	 * @instance
	 * @param {string} type The type of alert, e.g. 'warning' or 'disallow'
	 * @param {string} message Message to show in the AbuseFilter overlay
	 */
	show: function ( type, message ) {
		var msg;

		// OverlayManager will replace previous instance of the route if present
		this.overlayManager.add( /^\/abusefilter$/, function () {
			return new AbuseFilterOverlay( {
				message: message
			} );
		} );

		if ( type === 'warning' ) {
			msg = mw.msg( 'mobile-frontend-editor-abusefilter-warning' );
		} else if ( type === 'disallow' ) {
			msg = mw.msg( 'mobile-frontend-editor-abusefilter-disallow' );
			this.isDisallowed = true;
		}

		this.$el.find( '.message p' ).text( msg );
		this.$el.removeClass( 'hidden' );
	},

	/**
	 * Hide the panel.
	 * @memberof AbuseFilterPanel
	 * @instance
	 */
	hide: function () {
		this.$el.addClass( 'hidden' );
	}
} );

module.exports = AbuseFilterPanel;
'use strict';
var Drawer = require( '../mobile.startup/Drawer' ),
	Button = require( '../mobile.startup/Button' ),
	mfExtend = require( '../mobile.startup/mfExtend' ),
	Icon = require( '../mobile.startup/Icon' ),
	util = require( '../mobile.startup/util' );

/**
 * This creates the drawer at the bottom of the screen that appears when a
 * blocked user tries to edit.
 * @class BlockReason
 * @extends Drawer
 */
function BlockMessage() {
	Drawer.apply( this, arguments );
}

mfExtend( BlockMessage, Drawer, {
	/**
	 * @memberof BlockMessage
	 * @instance
	 */
	defaults: util.extend( {}, Drawer.prototype.defaults, {
		stopHandIcon: new Icon( {
			name: 'stop-hand'
		} ).options,
		userIcon: new Icon( {
			tagName: 'span',
			name: 'profile'
		} ).options,
		okButton: new Button( {
			label: mw.msg( 'ok' ),
			tagName: 'button',
			progressive: true,
			additionalClassNames: 'cancel'
		} ).options,
		createDetailsAnchorHref: function () {
			return mw.util.getUrl( 'Special:BlockList', { wpTarget: '#' + this.blockId } );
		},
		createDetailsAnchorLabel: function () {
			return mw.msg( 'mobile-frontend-editor-blocked-drawer-help' );
		},
		createTitle: function () {
			return this.partial ? mw.msg( 'mobile-frontend-editor-blocked-drawer-title-partial' ) : mw.msg( 'mobile-frontend-editor-blocked-drawer-title' );
		},
		reasonHeader: mw.msg( 'mobile-frontend-editor-blocked-drawer-reason-header' ),
		creatorHeader: function () {
			// The gender is the subject (the blockee) not the object (the blocker).
			return mw.msg( 'mobile-frontend-editor-blocked-drawer-creator-header',
				this.user.options.gender || 'unknown' );
		},
		expiryHeader: mw.msg( 'mobile-frontend-editor-blocked-drawer-expiry-header' )
	} ),
	/**
	 * @memberof BlockMessage
	 * @instance
	 */
	templatePartials: util.extend( {}, Drawer.prototype.templatePartials, {
		button: Button.prototype.template,
		icon: Icon.prototype.template
	} ),
	/**
	 * @inheritdoc
	 * @memberof BlockMessage
	 * @instance
	 */
	onShowDrawer: function () {
		var wiki = mw.config.get( 'wgDBname' );

		Drawer.prototype.onShowDrawer.apply( this );

		if ( mw.config.get( 'wgEnableBlockNoticeStats' ) ) {
			mw.track( 'counter.MediaWiki.BlockNotices.' + wiki + '.MobileFrontend.shown', 1 );
		}
	},
	/**
	 * @memberof BlockMessage
	 * @instance
	 */
	template: util.template( `
{{#collapseIcon}}{{>icon}}{{/collapseIcon}}
<div class="block-message">
  <div class="block-message-icon">
    {{#stopHandIcon}}{{>icon}}{{/stopHandIcon}}
  </div>
  <div class="block-message-info">
    <div class="block-message-item block-message-title">
      <h5>{{ createTitle }}</h5>
    </div>
    <div class="block-message-data">
      {{#reason}}
        <div class="block-message-item">
          <h6>{{ reasonHeader }}</h6>
          <div><strong>{{{ reason }}}</strong></div>
        </div>
      {{/reason}}
      <div class="block-message-item block-message-creator">
        <h6>{{ creatorHeader }}</h6>
        <div><strong><a href="{{ creator.url }}">{{#userIcon}}{{>icon}}{{/userIcon}}{{ creator.name }}</a></strong></div>
      </div>
      {{#expiry}}
        <div class="block-message-item">
          <h6>{{ expiryHeader }}</h6>
          <div><strong>{{#duration}}{{ duration }} / {{/duration}}{{ expiry }}</strong></div>
        </div>
      {{/expiry}}
    </div>
    <div class="block-message-item block-message-buttons">
      {{#okButton}}
        {{>button}}
      {{/okButton}}
      <a href="{{ createDetailsAnchorHref }}">{{ createDetailsAnchorLabel }}</a>
    </div>
  </div>
</div>
	` )
} );

module.exports = BlockMessage;
var util = require( '../mobile.startup/util' ),
	parseSaveError = require( './parseSaveError' ),
	actionParams = require( '../mobile.startup/actionParams' );

/**
 * API that helps save and retrieve page content
 * @class EditorGateway
 *
 * @param {Object} options Configuration options
 * @param {mw.Api} options.api an Api to use.
 * @param {string} options.title the title to edit
 * @param {number} options.sectionId the id of the section to operate edits on.
 * @param {number} [options.oldId] revision to operate on. If absent defaults to latest.
 * @param {boolean} [options.isNewPage] whether the page being created is new
 */
function EditorGateway( options ) {
	this.api = options.api;
	this.title = options.title;
	this.sectionId = options.sectionId;
	this.oldId = options.oldId;
	// return an empty section for new pages
	this.content = options.isNewPage ? '' : undefined;
	this.hasChanged = false;
}

EditorGateway.prototype = {
	/**
	 * Get the block (if there is one) from the result.
	 * @memberof EditorGateway
	 * @param {Object} pageObj
	 * @return {Object|null}
	 */
	getBlockInfo: function ( pageObj ) {
		var blockedError;

		if ( pageObj.actions &&
			pageObj.actions.edit &&
			Array.isArray( pageObj.actions.edit )
		) {
			pageObj.actions.edit.some( function ( error ) {
				if ( [ 'blocked', 'autoblocked' ].indexOf( error.code ) !== -1 ) {
					blockedError = error;
					return true;
				}
				return false;
			} );

			if ( blockedError && blockedError.data && blockedError.data.blockinfo ) {
				// Preload library used by EditorOverlay
				// to format block expiry datetime and duration
				mw.loader.load( 'moment' );

				return blockedError.data.blockinfo;
			}
		}

		return null;
	},
	/**
	 * Get the content of a page.
	 * @memberof EditorGateway
	 * @instance
	 * @return {jQuery.Promise}
	 */
	getContent: function () {
		var options,
			self = this;

		function resolve() {
			return util.Deferred().resolve( {
				text: self.content || '',
				blockinfo: self.blockinfo,
				userinfo: self.userinfo
			} );
		}

		if ( this.content !== undefined ) {
			return resolve();
		} else {
			options = actionParams( {
				prop: [ 'revisions', 'info' ],
				meta: 'userinfo',
				rvprop: [ 'content', 'timestamp' ],
				titles: self.title,
				// get block information for this user
				intestactions: 'edit',
				intestactionsdetail: 'full',
				uiprop: 'options'
			} );
			// Load text of old revision if desired
			if ( this.oldId ) {
				options.rvstartid = this.oldId;
			}
			// See Bug 50136 - passing rvsection will fail with non wikitext
			if ( util.isNumeric( this.sectionId ) ) {
				options.rvsection = this.sectionId;
			}
			return this.api.get( options ).then( function ( resp ) {
				var revision, pageObj;

				if ( resp.error ) {
					return util.Deferred().reject( resp.error.code );
				}

				pageObj = resp.query.pages[0];
				// page might not exist and caller might not have known.
				if ( pageObj.missing !== undefined ) {
					self.content = '';
				} else {
					revision = pageObj.revisions[0];
					self.content = revision.content;
					self.timestamp = revision.timestamp;
				}

				self.userinfo = resp.query.userinfo;

				// save content a second time to be able to check for changes
				self.originalContent = self.content;
				self.blockinfo = self.getBlockInfo( pageObj );

				return resolve();
			} );
		}
	},

	/**
	 * Mark content as modified and set changes to be submitted when #save
	 * is invoked.
	 * @memberof EditorGateway
	 * @instance
	 * @param {string} content New section content.
	 */
	setContent: function ( content ) {
		if ( this.originalContent !== content ) {
			this.hasChanged = true;
		} else {
			this.hasChanged = false;
		}
		this.content = content;
	},

	/**
	 * Mark content as modified and set text that should be prepended to given
	 * section when #save is invoked.
	 * @memberof EditorGateway
	 * @instance
	 * @param {string} text Text to be prepended.
	 */
	setPrependText: function ( text ) {
		this.prependtext = text;
		this.hasChanged = true;
	},

	/**
	 * Save the new content of the section, previously set using #setContent.
	 * @memberof EditorGateway
	 * @instance
	 * @param {Object} options Configuration options
	 * @param {string} [options.summary] Optional summary for the edit.
	 * @param {string} [options.captchaId] If CAPTCHA was requested, ID of the
	 * captcha.
	 * @param {string} [options.captchaWord] If CAPTCHA was requested, term
	 * displayed in the CAPTCHA.
	 * @return {jQuery.Deferred} On failure callback is passed an object with
	 * `type` and `details` properties. `type` is a string describing the type
	 * of error, `details` can be any object (usually error message).
	 */
	save: function ( options ) {
		var self = this,
			result = util.Deferred();

		options = options || {};

		/**
		 * Save content. Make an API request.
		 * @return {jQuery.Deferred}
		 */
		function saveContent() {
			var apiOptions = {
				action: 'edit',
				title: self.title,
				summary: options.summary,
				captchaid: options.captchaId,
				captchaword: options.captchaWord,
				basetimestamp: self.timestamp,
				starttimestamp: self.timestamp
			};

			if ( self.content !== undefined ) {
				apiOptions.text = self.content;
			} else if ( self.prependtext ) {
				apiOptions.prependtext = self.prependtext;
			}

			if ( util.isNumeric( self.sectionId ) ) {
				apiOptions.section = self.sectionId;
			}

			self.api.postWithToken( 'csrf', apiOptions ).then( function ( data ) {
				if ( data && data.edit && data.edit.result === 'Success' ) {
					self.hasChanged = false;
					result.resolve();
				} else {
					result.reject( parseSaveError( data ) );
				}
			}, function ( code, data ) {
				result.reject( parseSaveError( data, code || 'unknown' ) );
			} );
			return result;
		}

		return saveContent();
	},

	/**
	 * Abort any pending previews.
	 * @memberof EditorGateway
	 * @instance
	 */
	abortPreview: function () {
		if ( this._pending ) {
			this._pending.abort();
		}
	},

	/**
	 * Get page preview from the API and abort any existing previews.
	 * @memberof EditorGateway
	 * @instance
	 * @param {Object} options API query parameters
	 * @return {jQuery.Deferred}
	 */
	getPreview: function ( options ) {
		var result = util.Deferred(),
			sectionLine = '',
			sectionId = '',
			request,
			self = this;

		util.extend( options, {
			action: 'parse',
			// Enable section preview mode to avoid errors (bug 49218)
			sectionpreview: true,
			// needed for pre-save transform to work (bug 53692)
			pst: true,
			// Output mobile HTML (bug 54243)
			mobileformat: true,
			title: this.title,
			prop: [ 'text', 'sections' ]
		} );

		this.abortPreview();

		request = this.api.post( options );
		this._pending = request.then( function ( resp ) {
			if ( resp && resp.parse && resp.parse.text ) {
				// section 0 haven't a section name so skip
				if ( self.sectionId !== 0 &&
					resp.parse.sections !== undefined &&
					resp.parse.sections[0] !== undefined
				) {
					if ( resp.parse.sections[0].anchor !== undefined ) {
						sectionId = resp.parse.sections[0].anchor;
					}
					if ( resp.parse.sections[0].line !== undefined ) {
						sectionLine = resp.parse.sections[0].line;
					}
				}
				result.resolve( {
					text: resp.parse.text['*'],
					id: sectionId,
					line: sectionLine
				} );
			} else {
				result.reject();
			}
		}, function () {
			result.reject();
		} ).promise( {
			abort: function () { request.abort(); }
		} );

		return result;
	}
};

module.exports = EditorGateway;
var EditorOverlayBase = require( './EditorOverlayBase' ),
	util = require( '../mobile.startup/util' ),
	Section = require( '../mobile.startup/Section' ),
	saveFailureMessage = require( './saveFailureMessage' ),
	EditorGateway = require( './EditorGateway' ),
	AbuseFilterPanel = require( './AbuseFilterPanel' ),
	Button = require( '../mobile.startup/Button' ),
	mfExtend = require( '../mobile.startup/mfExtend' ),
	BlockMessage = require( './BlockMessage' ),
	VisualEditorOverlay = require( './VisualEditorOverlay' ),
	MessageBox = require( '../mobile.startup/MessageBox' );

/**
 * Overlay that shows an editor
 * @class EditorOverlay
 * @uses Section
 * @uses AbuseFilterPanel
 * @uses EditorGateway
 * @uses VisualEditorOverlay
 * @extends EditorOverlayBase
 *
 * @param {Object} options Configuration options
 */
function EditorOverlay( options ) {
	this.isFirefox = /firefox/i.test( window.navigator.userAgent );
	this.gateway = new EditorGateway( {
		api: options.api,
		title: options.title,
		sectionId: options.sectionId,
		oldId: options.oldId,
		isNewPage: options.isNewPage
	} );
	this.readOnly = !!options.oldId; // If old revision, readOnly mode
	if ( this.isVisualEditorEnabled() ) {
		options.editSwitcher = true;
	}
	if ( this.readOnly ) {
		options.readOnly = true;
		options.editingMsg = mw.msg( 'mobile-frontend-editor-viewing-source-page', options.title );
	} else {
		options.editingMsg = mw.msg( 'mobile-frontend-editor-editing-page', options.title );
	}
	// be explicit here. This may have been initialized from VE.
	options.isVisualEditor = false;
	options.previewingMsg = mw.msg( 'mobile-frontend-editor-previewing-page', options.title );
	EditorOverlayBase.call(
		this,
		util.extend( true,
			{ events: { 'input .wikitext-editor': 'onInputWikitextEditor' } },
			options
		)
	);
}

mfExtend( EditorOverlay, EditorOverlayBase, {
	/**
	 * @inheritdoc
	 * @memberof EditorOverlay
	 * @instance
	 */
	templatePartials: util.extend( {}, EditorOverlayBase.prototype.templatePartials, {
		content: util.template( `
<div lang="{{contentLang}}" dir="{{contentDir}}">
	<textarea class="wikitext-editor" id="wikitext-editor" cols="40" rows="10" placeholder="{{placeholder}}"></textarea>
	<div class="preview content"></div>
	{{#isAnon}}
	<div class="anonwarning content">
		<div>
			<!-- warning will go here -->
			<div class="actions"></div>
		</div>
	</div>
	{{/isAnon}}
</div>
		` )
	} ),
	/**
	 * @memberof EditorOverlay
	 * @instance
	 */
	editor: 'wikitext',
	/**
	 * @memberof EditorOverlay
	 * @instance
	 */
	sectionLine: '',

	/**
	 * Check whether VisualEditor is enabled or not.
	 * @memberof EditorOverlay
	 * @instance
	 * @return {boolean}
	 */
	isVisualEditorEnabled: function () {
		var ns = mw.config.get( 'wgVisualEditorConfig' ) &&
			mw.config.get( 'wgVisualEditorConfig' ).namespaces;

		return ns &&
			ns.indexOf(
				mw.config.get( 'wgNamespaceNumber' )
			) > -1 &&
			mw.config.get( 'wgTranslatePageTranslation' ) !== 'translation' &&
			mw.config.get( 'wgPageContentModel' ) === 'wikitext';
	},
	/**
	 * Wikitext Editor input handler
	 * @memberof EditorOverlay
	 * @instance
	 */
	onInputWikitextEditor: function () {
		this.gateway.setContent( this.$el.find( '.wikitext-editor' ).val() );
		this.$el.find( '.continue, .submit' ).prop( 'disabled', false );
	},
	/**
	 * @inheritdoc
	 * @memberof EditorOverlay
	 * @instance
	 */
	onClickContinue: function ( ev ) {
		// handle the click on "Edit without logging in"
		if ( this.options.isAnon && this.$el.find( ev.target ).hasClass( 'anonymous' ) ) {
			this._showEditorAfterWarning();
			return false;
		}
		EditorOverlayBase.prototype.onClickContinue.apply( this, arguments );
	},
	/**
	 * @inheritdoc
	 * @memberof EditorOverlay
	 * @instance
	 */
	onClickBack: function () {
		EditorOverlayBase.prototype.onClickBack.apply( this, arguments );
		this._hidePreview();
	},
	/**
	 * @inheritdoc
	 * @memberof EditorOverlay
	 * @instance
	 */
	postRender: function () {
		var self = this,
			options = this.options,
			$anonWarning = this.$el.find( '.anonwarning' );

		if ( this.isVisualEditorEnabled() ) {
			mw.loader.using( 'ext.visualEditor.switching' ).then( function () {
				var switchToolbar,
					toolFactory = new OO.ui.ToolFactory(),
					toolGroupFactory = new OO.ui.ToolGroupFactory();

				toolFactory.register( mw.libs.ve.MWEditModeVisualTool );
				toolFactory.register( mw.libs.ve.MWEditModeSourceTool );
				switchToolbar = new OO.ui.Toolbar( toolFactory, toolGroupFactory, {
					classes: [ 'editor-switcher' ]
				} );

				switchToolbar.on( 'switchEditor', function ( mode ) {
					if ( mode === 'visual' ) {
						// If the user tries to switch to the VisualEditor,
						// check if any changes have been made,
						// and if so, tell the user they have to save first.
						if ( !self.gateway.hasChanged ) {
							// TODO: Be more selective in which options we pass between editors
							self._switchToVisualEditor( self.options );
						} else {
							// TODO: Replace with an OOUI dialog
							if ( window.confirm( mw.msg( 'mobile-frontend-editor-switch-confirm' ) ) ) {
								self.onStageChanges();
							}
						}
					}
				} );

				switchToolbar.setup( [
					{
						name: 'editMode',
						type: 'list',
						icon: 'edit',
						title: mw.msg( 'visualeditor-mweditmode-tooltip' ),
						include: [ 'editModeVisual', 'editModeSource' ]
					}
				] );

				self.$el.find( '.switcher-container' ).html( switchToolbar.$element );
				switchToolbar.emit( 'updateState' );
			} );
		}

		EditorOverlayBase.prototype.postRender.apply( this );

		this.$preview = this.$el.find( '.preview' );
		this.$content = this.$el.find( '.wikitext-editor' );
		this.$content.addClass( 'mw-editfont-' + mw.user.options.get( 'editfont' ) );
		if ( options.isAnon ) {
			// add anonymous warning actions if present
			if ( $anonWarning.length ) {
				$anonWarning.find( ' > div' ).prepend(
					new MessageBox( {
						className: 'warningbox anon-msg',
						msg: mw.msg( 'mobile-frontend-editor-anonwarning' )
					} ).$el
				);
				if ( options.isAnon ) {
					this._renderAnonWarning( $anonWarning, options );
				}
			}
			this.$anonWarning = $anonWarning;
			this.$content.hide();
			// the user has to click login, signup or edit without login,
			// disable "Next" button on top right
			this.$anonHiddenButtons = this.$el.find( '.overlay-header .continue, .editor-switcher' ).hide();
			this.hideSpinner();
		}
		// make license links open in separate tabs
		this.$el.find( '.license a' ).attr( 'target', '_blank' );

		this.abuseFilterPanel = new AbuseFilterPanel( {
			overlayManager: this.overlayManager
		} ).appendTo( this.$el.find( '.panels' ) );

		// If in readOnly mode, make textarea readonly
		if ( this.readOnly ) {
			this.$content.prop( 'readonly', true );
		}

		this.$content.on( 'input', this._resizeEditor.bind( this ) );

		if ( !self.options.isAnon ) {
