var View = require( '../mobile.startup/View' ),
	mfExtend = require( '../mobile.startup/mfExtend' );

/**
 * Single photo item in gallery
 * @class PhotoItem
 * @extends View
 */
function PhotoItem() {
	View.apply( this, arguments );
}

mfExtend( PhotoItem, View, {
	/**
	 * @memberof PhotoItem
	 * @instance
	 */
	template: mw.template.get( 'mobile.special.uploads.scripts', 'PhotoItem.hogan' ),
	/**
	 * @memberof PhotoItem
	 * @instance
	 */
	tagName: 'li'
} );
module.exports = PhotoItem;
var icons = require( '../mobile.startup/icons' ),
	PhotoListGateway = require( './PhotoListGateway' ),
	PhotoItem = require( './PhotoItem' ),
	mfExtend = require( '../mobile.startup/mfExtend' ),
	ScrollEndEventEmitter = require( '../mobile.startup/ScrollEndEventEmitter' ),
	View = require( '../mobile.startup/View' );

/**
 * Creates a list of photo items
 * @class PhotoList
 * @uses PhotoListApi
 * @uses PhotoItem
 * @uses ScrollEndEventEmitter
 * @extends View
 *
 * @param {Object} options Configuration options
 * @param {OO.EventEmitter} options.eventBus Object used to listen for scroll:throttled events
 * @param {string} options.url for overriding default URI for API queries
 */
function PhotoList( options ) {
	var gatewayOptions = {
		url: options.url,
		api: options.api
	};

	if ( options.username ) {
		gatewayOptions.username = options.username;
	} else if ( options.category ) {
		gatewayOptions.category = options.category;
	}
	this.gateway = new PhotoListGateway( gatewayOptions );
	// Set up infinite scroll
	this.scrollEndEventEmitter = new ScrollEndEventEmitter( options.eventBus, 1000 );
	this.scrollEndEventEmitter.on( ScrollEndEventEmitter.EVENT_SCROLL_END,
		this._loadPhotos.bind( this ) );
	View.call( this, options );
}

mfExtend( PhotoList, View, {
	/**
	 * @memberof PhotoList
	 * @instance
	 */
	template: mw.template.get( 'mobile.special.uploads.scripts', 'PhotoList.hogan' ),
	/**
	 * @memberof PhotoList
	 * @instance
	 * @mixes View#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {string} defaults.spinner HTML of the spinner icon.
	 * @property {mw.Api} defaults.api instance of an api
	 */
	defaults: {
		spinner: icons.spinner().toHtmlString()
	},
	/**
	 * @inheritdoc
	 * @memberof PhotoList
	 * @instance
	 */
	preRender: function () {
		// Disable until we've got the list rendered
		this.scrollEndEventEmitter.setElement( this.$el );
		this.scrollEndEventEmitter.disable();
	},
	/**
	 * @inheritdoc
	 * @memberof PhotoList
	 * @instance
	 */
	postRender: function () {
		this.$end = this.$( '.end' );
		this.$list = this.$( 'ul' );

		this._loadPhotos();
	},
	/**
	 * Check to see if the current view is an empty list.
	 * @memberof PhotoList
	 * @instance
	 * @return {boolean} whether no images have been rendered
	 */
	isEmpty: function () {
		return this.$list.find( 'li' ).length === 0;
	},
	/**
	 * Renders an empty message prior to the list.
	 * FIXME: Should be handled in template, not a method.
	 * @memberof PhotoList
	 * @instance
	 */
	showEmptyMessage: function () {
		this.parseHTML( '<p class="content empty">' ).text( mw.msg( 'mobile-frontend-donate-image-nouploads' ) )
			.insertBefore( this.$list );
	},
	/**
	 * Hides the message saying the list is empty
	 * FIXME: Should be handled in template, not a method.
	 * @memberof PhotoList
	 * @instance
	 */
	hideEmptyMessage: function () {
		this.$( '.empty' ).hide();
	},
	/**
	 * Shows loading spinner
	 * @memberof PhotoList
	 * @instance
	 */
	showSpinner: function () {
		this.$end.show();
	},
	/**
	 * Hides loading spinner
	 * @memberof PhotoList
	 * @instance
	 */
	hideSpinner: function () {
		this.$end.hide();
	},
	/**
	 * Shows/hides empty state if PhotoList is empty.
	 * @memberof PhotoList
	 * @instance
	 */
	updateEmptyUI: function () {
		if ( this.isEmpty() ) {
			this.showEmptyMessage();
		} else {
			this.hideEmptyMessage();
		}
	},
	/**
	 * Append an array of photos to the view.
	 * @memberof PhotoList
	 * @instance
	 * @param {Array} photosData Array of objects describing a new {PhotoItem}
	 */
	appendPhotos: function ( photosData ) {
		var self = this;
		photosData.forEach( function ( photo ) {
			new PhotoItem( photo ).appendTo( self.$list );
		} );
	},
	/**
	 * Enables infinite scroll if it's disabled
	 * @memberof PhotoList
	 * @instance
	 */
	enableScroll: function () {
		if ( this.scrollEndEventEmitter.enabled === false ) {
			this.scrollEndEventEmitter.enable();
		}
	},
	/**
	 * Load photos into the view using {{PhotoListApi}} when the end is near
	 * and no current API requests are underway.
	 * @memberof PhotoList
	 * @instance
	 * @private
	 */
	_loadPhotos: function () {
		var self = this;

		self.showSpinner();

		this.gateway.getPhotos().then( function ( response ) {
			var photos = response.photos || [],
				canContinue = response.canContinue;

			self.appendPhotos( photos );
			self.updateEmptyUI();
			if ( canContinue ) {
				self.enableScroll();
			}

			self.hideSpinner();
		} ).catch( function () {
			self.updateEmptyUI();
			self.hideSpinner();

			// try loading again if request failed
			self.enableScroll();
		} );
	}
} );

module.exports = PhotoList;
var util = require( '../mobile.startup/util' );

/**
 * API for retrieving gallery photos
 * @class PhotoListGateway
 *
 * @param {Object} options Configuration options
 * @param {mw.Api} options.api
 * @param {string} options.url for overriding default URI for API queries
 */
function PhotoListGateway( options ) {
	this.api = options.api;
	this.url = options.url;
	this.username = options.username;
	this.category = options.category;
	this.limit = 10;
	this.continueParams = {
		continue: ''
	};
	this.canContinue = true;
}

/**
 * Returns a description based on the file name using
 * a regular expression that strips the file type suffix,
 * namespace prefix and any
 * date suffix in format YYYY-MM-DD HH-MM
 * @memberof PhotoListGateway
 * @instance
 * @private
 * @param {string} title Title of file
 * @return {string} Description for file
 */
function getDescription( title ) {
	title = title.replace( /\.[^. ]+$/, '' ); // replace filename suffix
	// strip namespace: prefix and date suffix from remainder
	return title.replace( /^[^:]*:/, '' )
		.replace( / \d{4}-\d{1,2}-\d{1,2} \d{1,2}-\d{1,2}$/, '' );
}

PhotoListGateway.prototype = {
	/**
	 * Returns the value in pixels of a medium thumbnail
	 * @memberof PhotoListGateway
	 * @instance
	 * @return {number}
	 */
	getWidth: function () {
		return mw.config.get( 'wgMFThumbnailSizes' ).small;
	},
	/**
	 * Extracts image data from api response
	 * @memberof PhotoListGateway
	 * @instance
	 * @private
	 * @param {Object} page as returned by api request
	 * @return {Object} describing image.
	 */
	_getImageDataFromPage: function ( page ) {
		var img = page.imageinfo[0];
		return {
			url: img.thumburl,
			title: page.title,
			timestamp: img.timestamp,
			description: getDescription( page.title ),
			descriptionUrl: img.descriptionurl
		};
	},
	/**
	 * Get the associated query needed to retrieve images from API based
	 * on currently configured options.
	 * @memberof PhotoListGateway
	 * @instance
	 * @return {Object}
	 */
	getQuery: function () {
		var query = util.extend( {
			action: 'query',
			prop: 'imageinfo',
			// FIXME: [API] have to request timestamp since api returns an object
			// rather than an array thus we need a way to sort
			iiprop: 'url|timestamp',
			iiurlwidth: this.getWidth()
		}, this.continueParams );

		if ( this.username ) {
			util.extend( query, {
				generator: 'allimages',
				gaiuser: this.username,
				gaisort: 'timestamp',
				gaidir: 'descending',
				gailimit: this.limit
			} );
		} else if ( this.category ) {
			util.extend( query, {
				generator: 'categorymembers',
				gcmtitle: 'Category:' + this.category,
				gcmtype: 'file',
				// FIXME [API] a lot of duplication follows due to the silly way generators work
				gcmdir: 'descending',
				gcmlimit: this.limit
			} );
		}

		if ( this.url ) {
			// A foreign api is being accessed! Enable anonymous CORS queries!
			query.origin = '*';
		}
		return query;
	},
	/**
	 * Request photos beginning with the current value of endTimestamp
	 * @memberof PhotoListGateway
	 * @instance
	 * @return {jQuery.Deferred} where parameter is a list of JavaScript
	 *  objects describing an image.
	 */
	getPhotos: function () {
		var self = this;

		return this.api.ajax( this.getQuery(), { url: this.url } ).then( function ( resp ) {
			var photos = [];
			if ( resp.query && resp.query.pages ) {
				// FIXME: [API] in an ideal world imageData would be a sorted array
				// but it is a map of {[id]: page}
				photos = Object.keys( resp.query.pages ).map( function ( id ) {
					return self._getImageDataFromPage( resp.query.pages[id] );
				} ).sort( function ( a, b ) {
					return a.timestamp < b.timestamp ? 1 : -1;
				} );
			}

			if ( resp.continue !== undefined ) {
				self.continueParams = resp.continue;
			} else {
				self.canContinue = false;
			}

			return {
				canContinue: self.canContinue,
				// FIXME: Should reply with a list of PhotoItem or Photo classes.
				photos: photos
			};
		} );
	}
};

PhotoListGateway.test = {
	getDescription: getDescription
};

module.exports = PhotoListGateway;
/* global $ */
var
	user = mw.user,
	PhotoList = require( './PhotoList' ),
	eventBus = require( '../mobile.startup/eventBusSingleton' ),
	pageParams = mw.config.get( 'wgPageName' ).split( '/' ),
	currentUserName = user.getName(),
	corsUrl = mw.config.get( 'wgMFPhotoUploadEndpoint' ) || undefined,
	userName = pageParams[1] ? pageParams[1] : currentUserName;

/**
 * Initialise a photo upload button at the top of the page.
 * @param {mw.Api} api
 */
function init( api ) {
	// check there are no errors on the page before attempting
	// we might have an invalid username
	if ( $( '.errorbox' ).length === 0 ) {
		new PhotoList( {
			url: corsUrl,
			api: api,
			username: userName,
			eventBus: eventBus
		} ).appendTo( '#mw-content-text .content' );
	}
}

// Assume we are on the special page.
if ( userName ) {
	$( function () {
		init( new mw.Api() );
	} );
}
