var
	mfExtend = require( './mfExtend' ),
	Drawer = require( './Drawer' ),
	util = require( './util' ),
	Button = require( './Button' ),
	Anchor = require( './Anchor' );

/**
 * @typedef {string|number|boolean|undefined} QueryVal
 * @typedef {Object.<string, QueryVal|QueryVal[]>} QueryParams
 *
 * @typedef {Object} Options
 * @prop {string} [returnTo]
 * @prop {QueryParams} [queryParams]
 * @prop {QueryParams} [signupQueryParams]
 * @prop {Object} [options.progressiveButton] template options for Button element for signing in
 * @prop {Object} [options.actionAnchor] template options for Anchor element for signing up
 */

/**
 * This creates the drawer at the bottom of the screen that appears when an anonymous
 * user tries to perform an action that requires being logged in. It presents the user
 * with options to log in or sign up for a new account.
 * @class CtaDrawer
 * @extends Drawer
 * @uses Button
 * @uses Icon
 * @uses Anchor
 * @param {Options} options
 */
function CtaDrawer( options ) {
	Drawer.call( this,
		util.extend( {}, Drawer.prototype.defaults, {
			progressiveButton: new Button( {
				progressive: true,
				label: mw.msg( 'mobile-frontend-watchlist-cta-button-login' )
			} ).options,
			actionAnchor: new Anchor( {
				progressive: true,
				label: mw.msg( 'mobile-frontend-watchlist-cta-button-signup' )
			} ).options
		}, options )
	);
}

mfExtend( CtaDrawer, Drawer, {
	/**
	 * @memberof CtaDrawer
	 * @instance
	 */
	templatePartials: util.extend( {}, Drawer.prototype.templatePartials, {
		button: Button.prototype.template,
		anchor: Anchor.prototype.template
	} ),
	/**
		 * @memberof CtaDrawer
		 * @instance
		 */
	template: mw.template.get( 'mobile.startup', 'CtaDrawer.hogan' ),
	/**
		 * @inheritdoc
		 * @memberof CtaDrawer
		 * @instance
		 */
	preRender: function () {
		var params = redirectParams( this.options.queryParams, this.options.returnTo );

		// Give the button and the anchor a default target, if it isn't set already. Buttons are
		// customized by Minerva's red link drawer, skins.minerva.scripts/init.js.
		if ( !this.options.progressiveButton.href ) {
			this.options.progressiveButton.href = mw.util.getUrl( 'Special:UserLogin', params );
		}
		if ( !this.options.actionAnchor.href ) {
			this.options.actionAnchor.href = mw.util.getUrl(
				'Special:UserLogin', signUpParams( params, this.options.signupQueryParams )
			);
		}
	}
} );

/**
 * Special:UserLogin post-request redirect query parameters.
 * @param {QueryParams} params
 * @param {string} [redirectURL]
 * @return {QueryParams}
 */
function redirectParams( params, redirectURL ) {
	return util.extend( {
		// use wgPageName as this includes the namespace if outside Main
		returnto: redirectURL || mw.config.get( 'wgPageName' )
	}, params );
}

/**
 * Special:UserLogin account creation query parameters.
 * @param {...QueryParams} params
 * @return {QueryParams}
 */
function signUpParams() {
	[].push.call( arguments, { type: 'signup' } );
	return util.extend.apply( util, arguments );
}

CtaDrawer.prototype.test = {
	redirectParams: redirectParams,
	signUpParams: signUpParams
};

module.exports = CtaDrawer;
var
	mfExtend = require( './mfExtend' ),
	Panel = require( './Panel' ),
	util = require( './util' ),
	Icon = require( './Icon' );

/**
 * A {@link View} that pops up from the bottom of the screen.
 * @class Drawer
 * @extends Panel
 * @param {Object} props
 */
function Drawer( props ) {
	Panel.call( this,
		util.extend( {
			className: 'drawer position-fixed'
		}, props )
	);
}

mfExtend( Drawer, Panel, {
	/**
	 * @memberof Drawer
	 * @instance
	 * @mixes Panel#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {string} defaults.cancelButton HTML of the button that closes the drawer.
	 */
	defaults: util.extend( {}, Panel.prototype.defaults, {
		// Used by CtaDrawer, BlockMessage.
		collapseIcon: new Icon( {
			name: 'arrow',
			additionalClassNames: 'cancel'
		} ).options
	} ),
	/**
	 * @memberof Drawer
	 * @instance
	 */
	templatePartials: util.extend( {}, Panel.prototype.templatePartials, {
		icon: Icon.prototype.template
	} ),
	/**
	 * Defines an element that the Drawer should automatically be appended to.
	 * @memberof Drawer
	 * @instance
	 * @property {string}
	 */
	appendToElement: 'body',
	/**
	 * Whether the drawer should disappear on a scroll event
	 * @memberof Drawer
	 * @instance
	 * @property {boolean}
	 */
	closeOnScroll: true,
	events: util.extend( {}, Panel.prototype.events, {
		click: 'stopPropagation'
	} ),

	/**
	 * @inheritdoc
	 * @memberof Drawer
	 * @instance
	 */
	postRender: function () {
		var self = this;
		// This module might be loaded at the top of the page e.g. Special:Uploads
		// Thus ensure we wait for the DOM to be loaded
		util.docReady( function () {
			self.appendTo( self.appendToElement );
			self.$el.parent().addClass( 'has-drawer' );
		} );
		this.on( 'show', this.onShowDrawer.bind( this ) );
		this.on( 'hide', this.onHideDrawer.bind( this ) );
	},
	/**
	 * Stop Propagation event handler
	 * @memberof Drawer
	 * @instance
	 * @param {Object} ev event object
	 * Allow the drawer itself to be clickable (e.g. for copying and pasting references
	 * clicking links in reference)
	 */
	stopPropagation: function ( ev ) {
		ev.stopPropagation();
	},

	/**
	 * ShowDrawer event handler
	 * @memberof Drawer
	 * @instance
	 */
	onShowDrawer: function () {
		var self = this;

		this.$el.parent().addClass( 'drawer-visible' );

		setTimeout( function () {
			var $window = util.getWindow();
			$window.one( 'click.drawer', self.hide.bind( self ) );
			if ( self.closeOnScroll ) {
				$window.one( 'scroll.drawer', self.hide.bind( self ) );
			}
		}, self.minHideDelay );
	},

	/**
	 * HideDrawer event handler
	 * @memberof Drawer
	 * @instance
	 */
	onHideDrawer: function () {
		this.$el.parent().removeClass( 'drawer-visible' );
		// .one() registers one callback for scroll and click independently
		// if one fired, get rid of the other one
		util.getWindow().off( '.drawer' );
	}
} );

module.exports = Drawer;
/* global $ */
var
	util = require( './util' ),
	mfExtend = require( './mfExtend' ),
	overlayManager = null;

/**
 * Manages opening and closing overlays when the URL hash changes to one
 * of the registered values (see OverlayManager.add()).
 *
 * This allows overlays to function like real pages, with similar browser back/forward
 * and refresh behavior.
 *
 * @class OverlayManager
 * @param {Router} router
 * @param {string} appendToSelector
 */
function OverlayManager( router, appendToSelector ) {
	router.on( 'route', this._checkRoute.bind( this ) );
	this.router = router;
	// use an object instead of an array for entries so that we don't
	// duplicate entries that already exist
	this.entries = {};
	// stack of all the open overlays, stack[0] is the latest one
	this.stack = [];
	this.hideCurrent = true;
	// Set the element that overlays will be appended to
	if ( !appendToSelector ) {
		mw.log.warn( 'appendToSelector will soon be a required parameter to OverlayManager' );
	}
	this.appendToSelector = appendToSelector || 'body';
}

mfExtend( OverlayManager, {
	/**
	 * Don't try to hide the active overlay on a route change event triggered
	 * by hiding another overlay.
	 * Called when hiding an overlay.
	 * @memberof OverlayManager
	 * @instance
	 * @private
	 */
	_onHideOverlay: function () {
		this.hideCurrent = false;

		this.router.back();
	},

	/** Attach overlay to DOM
	 * @memberof OverlayManager
	 * @instance
	 * @private
	 * @param {Overlay} overlay to attach
	*/
	_attachOverlay: function ( overlay ) {
		if ( !overlay.$el.parents().length ) {
			$( this.appendToSelector ).append( overlay.$el );
		}
	},
	/**
	 * Show the overlay and bind the '_om_hide' event to _onHideOverlay.
	 * @memberof OverlayManager
	 * @instance
	 * @private
	 * @param {Overlay} overlay to show
	 */
	_show: function ( overlay ) {

		// if hidden using overlay (not hardware) button, update the state
		overlay.once( '_om_hide', this._onHideOverlay.bind( this ) );

		this._attachOverlay( overlay );
		overlay.show();
	},

	/**
	 * Hide overlay
	 * @memberof OverlayManager
	 * @instance
	 * @private
	 * @param {Overlay} overlay to hide
	 * @return {boolean} Whether the overlay has been hidden
	 */
	_hideOverlay: function ( overlay ) {
		var result;

		// remove the callback for updating state when overlay closed using
		// overlay close button
		overlay.off( '_om_hide' );

		result = overlay.hide( this.stack.length > 1 );

		// if closing prevented, reattach the callback
		if ( !result ) {
			overlay.once( '_om_hide', this._onHideOverlay.bind( this ) );
		}

		return result;
	},

	/**
	 * Show match's overlay if match is not null.
	 * @memberof OverlayManager
	 * @instance
	 * @private
	 * @param {Object|null} match Object with factory function's result. null if no match.
	 */
	_processMatch: function ( match ) {
		var factoryResult,
			self = this;

		/**
		 * Attach an event to the overlays hide event
		 * @param {Overlay} overlay
		 */
		function attachHideEvent( overlay ) {
			overlay.on( 'hide', function () {
				overlay.emit( '_om_hide' );
			} );
		}

		if ( match ) {
			if ( match.overlay ) {
				// if the match is an overlay that was previously opened, reuse it
				self._show( match.overlay );
			} else {
				// else create an overlay using the factory function result (either
				// a promise or an overlay)
				factoryResult = match.factoryResult;
				// http://stackoverflow.com/a/13075985/365238
				if ( typeof factoryResult.promise === 'function' ) {
					factoryResult.then( function ( overlay ) {
						match.overlay = overlay;
						attachHideEvent( overlay );
						self._show( overlay );
					} );
				} else {
					match.overlay = factoryResult;
					attachHideEvent( match.overlay );
					self._show( factoryResult );
				}
			}
		}
	},

	/**
	 * A callback for Router's `route` event.
	 * @memberof OverlayManager
	 * @instance
	 * @private
	 * @param {jQuery.Event} ev Event object.
	 */
	_checkRoute: function ( ev ) {
		var
			current = this.stack[0],
			match;

		// When entering an overlay for the first time,
		// the manager should remember the user's scroll position
		// overlays always open at top of page
		// and we'll want to restore it later.
		// This should happen before the call to _matchRoute which will "show" the overlay.
		// The Overlay has similar logic for overlays that are not managed via the overlay.
		if ( !current ) {
			this.scrollTop = window.pageYOffset;
		}

		match = Object.keys( this.entries ).reduce( function ( m, id ) {
			return m || this._matchRoute( ev.path, this.entries[ id ] );
		}.bind( this ), null );

		// if there is an overlay in the stack and it's opened, try to close it
		if (
			current &&
			current.overlay !== undefined &&
			this.hideCurrent &&
			!this._hideOverlay( current.overlay )
		) {
			// if hide prevented, prevent route change event
			ev.preventDefault();
		} else if ( !match ) {
			// if hidden and no new matches, reset the stack
			this.stack = [];
			// restore the scroll position.
			window.scrollTo( window.pageXOffset, this.scrollTop );
		}

		this.hideCurrent = true;
		this._processMatch( match );
	},

	/**
	 * Check if a given path matches one of the entries.
	 * @memberof OverlayManager
	 * @instance
	 * @private
	 * @param {string} path Path (hash) to check.
	 * @param {Object} entry Entry object created in OverlayManager#add.
	 * @return {Object|null} Match object with factory function's result.
	 *  Returns null if no match.
	 */
	_matchRoute: function ( path, entry ) {
		var
			next,
			match = path.match( entry.route ),
			previous = this.stack[1],
			self = this;

		/**
		 * Returns object to add to stack
		 * @method
		 * @ignore
		 * @return {Object}
		 */
		function getNext() {
			return {
				path: path,
				factoryResult: entry.factory.apply( self, match.slice( 1 ) )
			};
		}

		if ( match ) {
			// if previous stacked overlay's path matches, assume we're going back
			// and reuse a previously opened overlay
			if ( previous && previous.path === path ) {
				if ( previous.overlay && previous.overlay.hasLoadError ) {
					self.stack.shift();
					// Loading of overlay failed so we want to replace it with a new
					// overlay (which will try to load successfully)
					self.stack[0] = getNext();
					return self.stack[0];
				}

				self.stack.shift();
				return previous;
			} else {
				next = getNext();
				if ( this.stack[0] && next.path === this.stack[0].path ) {
					// current overlay path is same as path to check which means overlay
					// is attempting to refresh so just replace current overlay with new
					// overlay
					self.stack[0] = next;
				} else {
					self.stack.unshift( next );
				}
				return next;
			}
		}

		return null;
	},

	/**
	 * Add an overlay that should be shown for a specific fragment identifier.
	 *
	 * The following code will display an overlay whenever a user visits a URL that
	 * end with '#/hi/name'. The value of `name` will be passed to the overlay.
	 *
	 *     @example
	 *     overlayManager.add( /\/hi\/(.*)/, function ( name ) {
	 *       var factoryResult = $.Deferred();
	 *
	 *       mw.using( 'mobile.HiOverlay', function () {
	 *         var HiOverlay = M.require( 'HiOverlay' );
	 *         factoryResult.resolve( new HiOverlay( { name: name } ) );
	 *       } );
	 *
	 *       return factoryResult;
	 *     } );
	 *
	 * @memberof OverlayManager
	 * @instance
	 * @param {RegExp} route route regular expression, optionally with parameters.
	 * @param {Function} factory a function returning an overlay or a $.Deferred
	 * which resolves to an overlay.
	 */
	add: function ( route, factory ) {
		var self = this,
			entry = {
				route: route,
				factory: factory
			};

		this.entries[route] = entry;
		// Check if overlay should be shown for the current path.
		// The DOM must fully load before we can show the overlay because Overlay relies on it.
		util.docReady( function () {
			self._processMatch( self._matchRoute( self.router.getPath(), entry ) );
		} );
	},

	/**
	 * Replace the currently displayed overlay with a new overlay without changing the
	 * URL. This is useful for when you want to switch overlays, but don't want to
	 * change the back button or close box behavior.
	 *
	 * @memberof OverlayManager
	 * @instance
	 * @param {Object} overlay The overlay to display
	 */
	replaceCurrent: function ( overlay ) {
		if ( this.stack.length === 0 ) {
			throw new Error( 'Trying to replace OverlayManager\'s current overlay, but stack is empty' );
		}
		this._hideOverlay( this.stack[0].overlay );
		this.stack[0].overlay = overlay;
		this._show( overlay );
	}
} );

/**
 * Retrieve a singleton instance using 'mediawiki.router'.
 * @memberof OverlayManager
 * @return {OverlayManager}
 */
OverlayManager.getSingleton = function () {
	if ( !overlayManager ) {
		overlayManager = new OverlayManager( mw.loader.require( 'mediawiki.router' ), 'body' );
	}
	return overlayManager;
};

module.exports = OverlayManager;
var
	HTML = mw.html,
	mfExtend = require( './mfExtend' ),
	time = require( './time' ),
	util = require( './util' ),
	Section = require( './Section' ),
	Thumbnail = require( './Thumbnail' ),
	View = require( './View' ),
	HEADING_SELECTOR = mw.config.get( 'wgMFMobileFormatterHeadings', [ 'h1', 'h2', 'h3', 'h4', 'h5' ] ).join( ',' ),
	BLACKLISTED_THUMBNAIL_CLASS_SELECTORS = [ 'noviewer', 'metadata' ];

/**
 * Mobile page view object
 *
 * @class Page
 * @uses Section
 * @extends View
 *
 * @param {Object} options Configuration options
 */
function Page( options ) {

	util.extend( this, {
		options: options,
		title: options.title,
		displayTitle: options.displayTitle,
		url: options.url || mw.util.getUrl( options.title ),
		id: options.id,
		wikidataDescription: options.wikidataDescription,
		thumbnail: ( Object.prototype.hasOwnProperty.call( options, 'thumbnail' ) ) ?
			options.thumbnail : false,
		isMissing: ( options.isMissing !== undefined ) ?
			options.isMissing : options.id === 0
	} );

	this.options.isBorderBox = false;
	this.options.languageUrl = mw.util.getUrl( 'Special:MobileLanguages/' + this.title );

	View.call( this, this.options );

	// Fallback if no displayTitle provided
	if ( !this.displayTitle ) {
		this.displayTitle = this.getDisplayTitle();
	}

	if ( this.thumbnail && this.thumbnail.width ) {
		this.thumbnail.isLandscape = this.thumbnail.width > this.thumbnail.height;
	}
}

mfExtend( Page, View, {
	/**
	 * @memberof Page
	 * @instance
	 * @mixes View#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {number} defaults.id Page ID. The default value of 0 represents a
	 * new or missing page. Be sure to override it to avoid side effects.
	 * @property {string} defaults.title Title of the page. It includes prefix where needed and
	 * is human readable, e.g. Talk:The man who lived.
	 * @property {string} defaults.displayTitle HTML title of the page for display. Falls back
	 * to defaults.title (escaped) if no value is provided. Must be safe HTML!
	 * @property {number} defaults.namespaceNumber the number of the
	 *  namespace the page belongs to
	 * @property {Object} defaults.protection List of permissions as returned by API,
	 * e.g. [{ edit: ['*'] }]
	 * @property {Array} defaults.sections Array of {Section} objects.
	 * @property {boolean} defaults.isMainPage Whether the page is the Main Page.
	 * @property {boolean} defaults.isMissing Whether the page exists in the wiki.
	 * @property {Object} defaults.thumbnail thumbnail definition corresponding to page image
	 * @property {boolean} defaults.thumbnail.isLandscape whether the image is in
	 *  landscape format
	 * @property {number} defaults.thumbnail.width of image in pixels.
	 * @property {number} defaults.thumbnail.height of image in pixels.
	 * @property {string} defaults.thumbnail.source url for image
	 */
	defaults: {
		id: 0,
		title: '',
		displayTitle: '',
		namespaceNumber: 0,
		protection: {
			edit: [ '*' ]
		},
		sections: [],
		isMissing: false,
		isMainPage: false,
		url: undefined,
		thumbnail: {
			isLandscape: undefined,
			source: undefined,
			width: undefined,
			height: undefined
		}
	},
	/**
	 * Retrieve the title that should be displayed to the user
	 * @memberof Page
	 * @instance
	 * @return {string} HTML
	 */
	getDisplayTitle: function () {
		return this.options.displayTitle || HTML.escape( this.options.title );
	},
	/**
	 * Determine if current page is in a specified namespace
	 * @memberof Page
	 * @instance
	 * @param {string} namespace Name of namespace
	 * @return {boolean}
	 */
	inNamespace: function ( namespace ) {
		return this.options.namespaceNumber === mw.config.get( 'wgNamespaceIds' )[namespace];
	},

	/**
	 * Find the heading in the page.
	 * This has the benefit of excluding any additional h2s and h3s that may
	 * have been added programatically.
	 * @method
	 * @param {number} sectionIndex as defined by the PHP parser.
	 *  It should correspond to the section id
	 *  used in the edit link for the section.
	 *  Note, confusingly, this is different from section "ID" which is
	 * used in methods
	 * @return {jQuery.Object}
	 */
	findSectionHeadingByIndex: function ( sectionIndex ) {
		if ( sectionIndex < 1 ) {
			// negative indexes will search from the end, which is behaviour we do not want.
			// return an empty set when this happens.
			return this.$();
		} else {
			return this.$( HEADING_SELECTOR )
				// Headings must strictly be a child element of a section element
				// or the parser-output.
				// Not an ancestor!
				.filter( '.mw-parser-output > *, [class^="mf-section-"] > *' ).eq( sectionIndex - 1 );
		}
	},
	/**
	 * Finds all child elements that match the selector in a given section or subsection.
	 * Returns any direct child elements that match the selector,
	 * (i.e. searches only one level deep)
	 * as well as any elements that match the selector within those children.
	 * If the Page has no headings (e.g. a stub),
	 * then the search will target all nodes within the page.
	 *
	 * This code should work on desktop (PHP parser HTML)
	 * as well as mobile formatted HTML (PHP parser + MobileFormatter)
	 * @method
	 * @param {number} sectionIndex as defined by the PHP parser. It should correspond to
	 *  the section id used in the edit link for the section.
	 *  Note, confusingly, this is different from section "ID" which is
	 *  used in methods
	 * @param {string} selector to match
	 * @return {jQuery.Object}
	 */
	findChildInSectionLead: function ( sectionIndex, selector ) {
		var $heading, $nextHeading, $container, $lead,
			headingSelector = HEADING_SELECTOR;

		function withNestedChildren( $matchingNodes ) {
			return $matchingNodes.find( selector ).addBack();
		}

		if ( sectionIndex === 0 ) {
			// lead is easy
			$lead = this.getLeadSectionElement();
			if ( $lead && $lead.length ) {
				return withNestedChildren( $lead.children( selector ) );
			} else {
				$heading = this.findSectionHeadingByIndex( 1 );
				return $heading.length ? withNestedChildren( $heading.prevAll( selector ) ) :
					// this page is a stub so search entire page
					this.$( selector );
			}
		}

		// find heading associated with the section by looking at its
		// index position in the article
		// section ids relate to the element position in the page and the first heading
		// lead has been dealt with above, so first heading corresponds to section 1,
		// the first heading in the article.
		$heading = this.findSectionHeadingByIndex( sectionIndex );

		// If section-heading is present on the heading,
		// then we know the page has been MobileFormatted
		// and that this is a wrapped section
		if ( $heading.hasClass( 'section-heading' ) ) {
			// get content of section
			$container = $heading.next();
			// inside section find the first heading
			$nextHeading = $container.find( headingSelector ).eq( 0 );
			return $nextHeading.length ?
				// find all amboxes before the next heading
				withNestedChildren( $nextHeading.prevAll( selector ) ) :
				// There is no subheadings inside
				// Grab all issues in section
				withNestedChildren( $container.children( selector ) );
		} else {
			// the heading relates to a subsection (or unwrapped desktop section),
			// so grab elements between this and the next one
			$nextHeading = $heading.eq( 0 ).nextAll( headingSelector ).eq( 0 );
			return $heading.nextUntil( $nextHeading, selector );
		}
	},

	/**
	 * Get the lead section of the page view.
	 * @memberof Page
	 * @instance
	 * @return {jQuery.Object|null}
	 */
	getLeadSectionElement: function () {
		/*
		 * The page is formatted as follows:
		 * <div id="bodyContent">
		 *   <!-- content of the page.. -->
		 *   <div id="mw-content-text">
		 *     <div class="mf-section-0">lead section</div>
		 *     <h2></h2>
		 *     <div class="mf-section-1">second section</div>
		 *   </div>
		 * </div>
		 */
		if ( this.$( '.mf-section-0' ).length ) {
			return this.$( '.mf-section-0' );
		}
		// no lead section found
		return null;
	},

	/**
	 * Determines if content model is wikitext
	 * @memberof Page
	 * @instance
	 * @return {boolean}
	 */
	isWikiText: function () {
		return mw.config.get( 'wgPageContentModel' ) === 'wikitext';
	},

	/**
	 * Checks whether the current page is the main page
	 * @memberof Page
	 * @instance
	 * @return {boolean}
	 */
