/**
 * Return the language code of the device in lowercase
 *
 * @ignore
 * @param {window.navigator} navigator
 * @return {string|undefined}
 */
function getDeviceLanguage( navigator ) {
	const lang = navigator.languages ?
		navigator.languages[ 0 ] :
		navigator.language || navigator.userLanguage ||
				navigator.browserLanguage || navigator.systemLanguage;

	return lang ? lang.toLowerCase() : undefined;
}

module.exports = getDeviceLanguage;
const
	m = require( '../moduleLoaderSingleton' ),
	getDeviceLanguage = require( './getDeviceLanguage' ),
	Overlay = require( '../Overlay' ),
	promisedView = require( '../promisedView' );

/**
 * @ignore
 * @param {PageGateway} gateway
 * @return {jQuery.Promise} Resolves to LanguageSearcher
 */
function loadLanguageSearcher( gateway ) {
	return mw.loader.using( 'mobile.languages.structured' ).then( function () {
		return gateway.getPageLanguages( mw.config.get( 'wgPageName' ), mw.config.get( 'wgUserLanguage' ) );
	} ).then( function ( data ) {
		const LanguageSearcher = m.require( 'mobile.languages.structured/LanguageSearcher' );

		return new LanguageSearcher( {
			languages: data.languages,
			variants: data.variants,
			deviceLanguage: getDeviceLanguage( navigator )
		} );

	} );
}

/**
 * Factory function that returns a language featured instance of an Overlay
 *
 * @param {PageGateway} pageGateway
 * @return {Overlay}
 */
function languageOverlay( pageGateway ) {
	return Overlay.make(
		{
			heading: mw.msg( 'mobile-frontend-language-heading' ),
			className: 'overlay language-overlay'
		}, promisedView( loadLanguageSearcher( pageGateway ) )
	);
}

// To make knowing when async logic has resolved easier in tests
languageOverlay.test = {
	loadLanguageSearcher: loadLanguageSearcher
};

module.exports = languageOverlay;
var
	icons = require( './icons' ),
	Overlay = require( './Overlay' );

/**
 * Overlay that initially shows loading animation until
 * caller hides it with .hide()
 *
 * @return {Overlay}
 */
function loadingOverlay() {
	var overlay = new Overlay( {
		className: 'overlay overlay-loading',
		noHeader: true
	} );
	icons.spinner().$el.appendTo( overlay.$el.find( '.overlay-content' ) );
	return overlay;
}

module.exports = loadingOverlay;
var m = require( '../moduleLoaderSingleton' ),
	promisedView = require( '../promisedView' ),
	util = require( '../util' ),
	header = require( '../headers' ).header,
	icons = require( '../icons' ),
	Overlay = require( '../Overlay' );

/**
 * Produce an overlay for mediaViewer
 * @param {Object} options
 * @return {Overlay}
 */
function mediaViewerOverlay( options ) {
	var overlay = Overlay.make(
		{
			headers: [
				header( '', [], icons.cancel( 'gray' ) )
			],
			className: 'overlay media-viewer'
		},
		promisedView(
			util.Promise.all( [
				mw.loader.using( 'mobile.mediaViewer' )
			] ).then( function () {
				var ImageCarousel = m.require( 'mobile.mediaViewer' ).ImageCarousel;
				return new ImageCarousel( options );
			} )
		)
	);

	return overlay;
}

module.exports = mediaViewerOverlay;
var
	moduleLoader = require( './moduleLoaderSingleton' ),
	util = require( './util' ),
	schemaMobileWebSearch = require( './search/schemaMobileWebSearch' );

// Expose the entry chunk through libraryTarget and library. This allows
// arbitrary file access via ResourceLoader like
// `mfModules['mobile.startup'].moduleLoader.require('mobile.startup/LoadingOverlay')`.
module.exports = {
	moduleLoader: moduleLoader,
	mfExtend: require( './mfExtend' ),
	context: require( './context' ),
	time: require( './time' ),
	util,
	View: require( './View' ),
	PageGateway: require( './PageGateway' ),
	Browser: require( './Browser' ),
	Button: require( './Button' ),
	Icon: require( './Icon' ),
	ReferencesGateway: require( './references/ReferencesGateway' ),
	ReferencesHtmlScraperGateway: require( './references/ReferencesHtmlScraperGateway' ),
	icons: require( './icons' ),
	Page: require( './Page' ),
	currentPage: require( './currentPage' ),
	PageHTMLParser: require( './PageHTMLParser' ),
	currentPageHTMLParser: require( './currentPageHTMLParser' ),
	Anchor: require( './Anchor' ),
	Skin: require( './Skin' ),
	OverlayManager: require( './OverlayManager' ),
	Overlay: require( './Overlay' ),
	loadingOverlay: require( './loadingOverlay' ),
	CtaDrawer: require( './CtaDrawer' ),
	toast: require( './toast' ),
	Watchstar: require( './watchstar/Watchstar' ),
	rlModuleLoader: require( './rlModuleLoader' ),
	eventBusSingleton: require( './eventBusSingleton' ),
	Toggler: require( './Toggler' ),
	toc: {
		// In case any old version of Minerva is cached.
		TableOfContents: () => {
			return {
				// in the unlikely event old Minerva code got loaded with new MF.
				$el: util.parseHTML( '<div>' )
			};
		}
	},
	notifications: {
		overlay: require( './notifications/overlay' )
	},
	references: require( './references/references' ),
	search: {
		SearchOverlay: require( './search/SearchOverlay' ),
		MobileWebSearchLogger: require( './search/MobileWebSearchLogger' ),
		SearchGateway: require( './search/SearchGateway' )
	},
	lazyImages: {
		lazyImageLoader: require( './lazyImages/lazyImageLoader' )
	},
	talk: {
		overlay: require( './talk/overlay' )
	},
	languageOverlay: require( './languageOverlay/languageOverlay' ),
	mediaViewer: {
		overlay: require( './mediaViewer/overlay' )
	}
};

mw.mobileFrontend = moduleLoader;

// Setup a single export for new modules to fold all of the above lines into.
// One export to rule them all!
moduleLoader.define( 'mobile.startup', module.exports );

schemaMobileWebSearch.subscribeMobileWebSearchSchema();
var Overlay = require( '../Overlay' ),
	m = require( '../moduleLoaderSingleton' ),
	promisedView = require( '../promisedView' ),
	View = require( '../View' ),
	Anchor = require( '../Anchor' );

/**
 * This callback is displayed as a global member.
 * @callback countChangeCallback
 * @param {number} count a capped (0-99 or 99+) count
 */

/**
 * Make a notification overlay
 *
 * @param {countChangeCallback} onCountChange receives one parameter - a capped (0-99 or 99+) count.
 * @param {Function} onNotificationListRendered a function that is called when the
 *   notifications list has fully rendered (taking no arguments)
 * @return {Overlay}
 */
function notificationsOverlay( onCountChange, onNotificationListRendered ) {
	var markAllReadButton,
		oouiPromise = mw.loader.using( 'mobile.notifications.overlay' ).then( function () {
			markAllReadButton = new OO.ui.ButtonWidget( {
				icon: 'checkAll',
				title: mw.msg( 'echo-mark-all-as-read' )
			} );
			return View.make(
				{ class: 'notifications-overlay-header-markAllRead' },
				[ markAllReadButton.$element ]
			);
		} ),
		markAllReadButtonView = promisedView( oouiPromise );
	// hide the button spinner as it is confusing to see in the top right corner
	markAllReadButtonView.$el.hide();

	const overlay = Overlay.make(
		{
			heading: '<strong>' + mw.message( 'notifications' ).escaped() + '</strong>',
			footerAnchor: new Anchor( {
				href: mw.util.getUrl( 'Special:Notifications' ),
				progressive: true,
				additionalClassNames: 'footer-link notifications-archive-link',
				label: mw.msg( 'echo-overlay-link' )
			} ).options,
			headerActions: [ markAllReadButtonView ],
			isBorderBox: false,
			className: 'overlay notifications-overlay navigation-drawer',
			onBeforeExit: ( exit ) => onBeforeExit( overlay, exit )
		},
		promisedView(
			oouiPromise.then( function () {
				const module = m.require( 'mobile.notifications.overlay' ),
					list = module.list;
				return list( module.echo(), markAllReadButton, onCountChange,
					onNotificationListRendered );
			} )
		)
	);
	return overlay;
}

/**
 * @param {Overlay} overlay
 * @param {Function} exit
 * @return {void}
 */
function onBeforeExit( overlay, exit ) {
	if ( 'transition' in overlay.$el[0].style ) {
		// Manually detach the overlay from DOM once hide animation completes.
		overlay.$el[0].addEventListener( 'transitionend', exit, { once: true } );

		// Kick off animation.
		overlay.$el[0].classList.remove( 'visible' );
	} else {
		exit();
	}
}

notificationsOverlay.test = { onBeforeExit };
module.exports = notificationsOverlay;
/**
 * Abstract base class
 * Gateway for retrieving references
 *
 * @class ReferencesGateway
 * @abstract
 *
 * @param {mw.Api} api
 */
function ReferencesGateway( api ) {
	this.api = api;
}

/**
 * Return the matched reference via API or DOM query
 *
 * @memberof ReferencesGateway
 * @instance
 * @param {string} id CSS selector
 * @param {Page} page to find reference for
 * @param {PageHTMLParser} pageHTMLParser
 * @return {jQuery.Promise} resolves with an Object representing reference
 *  with a `text` property
 *  The promise should be rejected with ReferenceGateway.ERROR_NOT_EXIST:
 *  if the reference is not found.
 *  If for some reason locating the reference fails return ReferenceGateway.ERROR_OTHER.
 */
ReferencesGateway.prototype.getReference = null;

/**
 * @memberof ReferencesGateway
 * @property ERROR_NOT_EXIST error code to be returned by getReference
 *  when a reference does not exist.
 */
ReferencesGateway.ERROR_NOT_EXIST = 'NOT_EXIST_ERROR';
/**
 * @memberof ReferencesGateway
 * @property ERROR_OTHER error code to be returned by getReference
 *  under any other circumstance not covered
 *  by ERROR_NOT_EXIST. It should be used if it is unclear whether a reference exists or not.
 */
ReferencesGateway.ERROR_OTHER = 'OTHER_ERROR';

module.exports = ReferencesGateway;
var ReferencesGateway = require( './ReferencesGateway' ),
	mfExtend = require( './../mfExtend' ),
	util = require( './../util' );

/**
 * Gateway for retrieving references via the content of the Page
 *
 * @class ReferencesHtmlScraperGateway
 * @extends ReferencesGateway
 * @inheritdoc
 */
function ReferencesHtmlScraperGateway() {
	ReferencesGateway.apply( this, arguments );
}

mfExtend( ReferencesHtmlScraperGateway, ReferencesGateway, {
	/**
	 * @memberof ReferencesHtmlScraperGateway
	 * @property EXTERNAL_LINK_CLASS a CSS class to place on external links
	 * in addition to the default 'external' class.
	 */
	EXTERNAL_LINK_CLASS: 'external--reference',
	/**
	 * @memberof ReferencesHtmlScraperGateway
	 * @instance
	 * @param {string} id of a DOM element in the page with '#' prefix.
	 *  can be encoded or decoded.
	 * @param {jQuery.Object} $container to scan for an element
	 * @return {jQuery.Promise} that can be used by getReference
	 */
	getReferenceFromContainer: function ( id, $container ) {
		var $el,
			result = util.Deferred();

		$el = $container.find( '#' + util.escapeSelector( id.substr( 1 ) ) );
		if ( $el.length ) {
			$el.find( '.external' ).addClass( this.EXTERNAL_LINK_CLASS );
			result.resolve( { text: $el.html() } );
		} else {
			result.reject( ReferencesGateway.ERROR_NOT_EXIST );
		}
		return result.promise();
	},
	/**
	 * @inheritdoc
	 * @memberof ReferencesHtmlScraperGateway
	 * @instance
	 * @param {string} id
	 * @param {Page} page
	 * @param {PageHTMLParser} pageHTMLParser
	 */
	getReference: function ( id, page, pageHTMLParser ) {
		// If an id is not found it's possible the id passed needs decoding (per T188547).
		return this.getReferenceFromContainer( decodeURIComponent( id ), pageHTMLParser.$el.find( 'ol.references' ) );
	}
} );

module.exports = ReferencesHtmlScraperGateway;
var references,
	Drawer = require( '../Drawer' ),
	util = require( '../util' ),
	icons = require( '../icons' ),
	ReferencesGateway = require( './ReferencesGateway' ),
	Icon = require( '../Icon' );

/**
 * Create a callback for clicking references
 * @param {Function} onNestedReferenceClick
 * @return {Function}
 */
function makeOnNestedReferenceClickHandler( onNestedReferenceClick ) {
	return ( ev ) => {
		const target = ev.target;
		if ( target.tagName === 'A' ) {
			onNestedReferenceClick(
				target.getAttribute( 'href' ),
				target.textContent
			);
			// Don't hide the already shown drawer via propagation
			// and stop default scroll behaviour.
			return false;
		}
	};
}

/**
 * Drawer for references
 * @uses Icon
 * @param {Object} props
 * @param {boolean} [props.error] whether an error has occurred
 * @param {string} props.title of reference e.g [1]
 * @param {string} props.text is the HTML of the reference
 * @param {Function} [props.onNestedReferenceClick] callback for when a reference
 *  inside the reference is clicked.
 * @return {Drawer}
 */
function referenceDrawer( props ) {
	let containerClassName = props.error ? new Icon( {
		name: 'error',
		hasText: true,
		isSmall: true
	} ).getClassName() : '';
	return new Drawer(
		util.extend(
			{
				closeOnScroll: false,
				className: 'drawer position-fixed text references-drawer',
				events: {
					'click sup': props.onNestedReferenceClick &&
						makeOnNestedReferenceClickHandler( props.onNestedReferenceClick )
				},
				children: [
					util.parseHTML( '<div>' )
						.addClass( 'references-drawer__header' )
						.append( [
							new Icon( {
								isSmall: true,
								name: 'citation-invert',
								additionalClassNames: 'references-drawer__title',
								hasText: true,
								label: mw.msg( 'mobile-frontend-references-citation' )
							} ).$el,
							icons.cancel( 'gray' ).$el
						] ),
					util.parseHTML( '<div>' ).addClass( containerClassName ).append(
						util.parseHTML( '<sup>' ).text( props.title ),
						props.text ?
							util.parseHTML( '<span>' ).html( props.text ) :
							icons.spinner().$el
					)
				]
			},
			props
		)
	);
}

references = {
	test: {
		makeOnNestedReferenceClickHandler
	},
	referenceDrawer,
	/**
	 * Fetch and render nested reference upon click
	 * @param {string} id of the reference to be retrieved
	 * @param {Page} page to locate reference for
	 * @param {string} refNumber the number it identifies as in the page
	 * @param {PageHTMLParser} pageHTMLParser
	 * @param {Gateway} gateway
	 * @return {jQuery.Deferred}
	 */
	showReference: function ( id, page, refNumber, pageHTMLParser, gateway ) {
		return gateway.getReference( id, page, pageHTMLParser ).then( function ( reference ) {
			const drawer = referenceDrawer( {
				title: refNumber,
				text: reference.text,
				onNestedReferenceClick: function ( href, text ) {
					references.showReference(
						href,
						page,
						text,
						pageHTMLParser,
						gateway
					);
					drawer.$el.remove();
				}
			} );
			drawer.show();
		}, function ( err ) {
			// If non-existent reference nothing to do.
			if ( err === ReferencesGateway.ERROR_NOT_EXIST ) {
				return;
			}

			const drawer = referenceDrawer( {
				error: true,
				title: refNumber,
				text: mw.msg( 'mobile-frontend-references-citation-error' )
			} );
			drawer.show();
		} );
	}
};

module.exports = references;
var
	loadingOverlay = require( './loadingOverlay' ),
	util = require( './util' );

/**
 * Utility library for looking up details on the current user
 * @class loader
 * @singleton
 */
module.exports = {
	// Exported to support testing and stubbing
	newLoadingOverlay: function () {
		return loadingOverlay();
	},
	/**
	 * Loads a module via ResourceLoader
	 * and displays a full screen LoadingOverlay during load time.
	 * @memberof loader
	 * @instance
	 * @param {string} name ResourceLoader module name to load asynchronously.
	 * @param {boolean} delegateHide if true the caller is responsible for hiding
	 *  the intermediate loader.
	 * @param {boolean} [showLoadingOverlay] if false a loading overlay will be hidden while
	 *  loading the module. Defaults to true.
	 * @return {jQuery.Promise}
	 */
	loadModule: function ( name, delegateHide, showLoadingOverlay ) {
		var loadingOverlay = this.newLoadingOverlay();

		showLoadingOverlay = ( showLoadingOverlay !== undefined ) ? showLoadingOverlay : true;
		if ( showLoadingOverlay ) {
			loadingOverlay.appendTo( document.body );
			loadingOverlay.show();
		}

		function hideOverlayIfNeeded() {
			if ( !delegateHide && showLoadingOverlay ) {
				loadingOverlay.hide();
			}
		}
		return mw.loader.using( name ).then( function () {
			hideOverlayIfNeeded();

			return loadingOverlay;
		}, function () {
			hideOverlayIfNeeded();

			return util.Deferred().reject().promise();
		} );
	}
};
/**
 * Coordinates the logging of MobileWebSchema events.
 * Implements schema defined at https://meta.wikimedia.org/wiki/Schema:MobileWebSearch
 *
 * @class
 */
function MobileWebSearchLogger() {
	this.userSessionToken = null;
	this.searchSessionToken = null;
}

MobileWebSearchLogger.prototype = {
	/**
	 * Sets the internal state required to deal with logging user session
	 * data.
	 * @memberof MobileWebSearchLogger
	 * @instance
	 * @private
	 */
	_newUserSession: function () {
		this.userSessionToken = mw.user.generateRandomSessionId();
	},

	/**
	 * Sets the internal state required to deal with logging search session
	 * data.
	 * @memberof MobileWebSearchLogger
	 * @instance
	 * @private
	 */
	_newSearchSession: function () {
		this.searchSessionToken = mw.user.generateRandomSessionId();
		this.searchSessionCreatedAt = new Date().getTime();
	},

	/**
	 * Handles the 'search-show' event.
	 * @memberof MobileWebSearchLogger
	 * @instance
	 */
	onSearchShow: function () {
		this._newUserSession();
	},

	/**
	 * Handles the 'search-start' event.
	 * @memberof MobileWebSearchLogger
	 * @instance
	 */
	onSearchStart: function () {
		this._newSearchSession();
		mw.track( 'mf.schemaMobileWebSearch', {
			action: 'session-start',
			userSessionToken: this.userSessionToken,
			searchSessionToken: this.searchSessionToken,
			timeOffsetSinceStart: 0
		} );
	},

	/**
	 * Handles the 'search-results' event.
	 * @memberof MobileWebSearchLogger
	 * @instance
	 * @param {Object} event with property {Object[]} event.results
	 */
	onSearchResults: function ( event ) {
		var timeOffsetSinceStart =
			new Date().getTime() - this.searchSessionCreatedAt;

		mw.track( 'mf.schemaMobileWebSearch', {
			action: 'impression-results',
			resultSetType: 'prefix',
			numberOfResults: event.results.length,
			userSessionToken: this.userSessionToken,
			searchSessionToken: this.searchSessionToken,
			// FIXME: Unless I'm mistaken, the timeToDisplayResults
			// property isn't necessary.
			timeToDisplayResults: timeOffsetSinceStart,
			timeOffsetSinceStart: timeOffsetSinceStart
		} );
	},

	/**
	 * Handles the 'search-result-click' event.
	 * @memberof MobileWebSearchLogger
	 * @instance
	 * @param {Object} event with property {number} event.index
	 *  The zero-based index of the result in the set of results.
	 */
	onSearchResultClick: function ( event ) {
		var timeOffsetSinceStart =
			new Date().getTime() - this.searchSessionCreatedAt;

		mw.track( 'mf.schemaMobileWebSearch', {
			action: 'click-result',
			// NOTE: clickIndex is 1-based.
			clickIndex: event.resultIndex + 1,
			userSessionToken: this.userSessionToken,
			searchSessionToken: this.searchSessionToken,
			timeOffsetSinceStart: timeOffsetSinceStart
		} );
	}
};

/**
 * Convenience function that wires up an instance of the
 * MobileWebSearchLogger class to the search-* events emitted by the
 * search overlay.
 * @memberof MobileWebSearchLogger
 * @param {SearchOverlay} searchOverlay
 */
MobileWebSearchLogger.register = function ( searchOverlay ) {
	var logger = new MobileWebSearchLogger();

	searchOverlay.on( 'search-show', logger.onSearchShow.bind( logger ) );
	searchOverlay.on( 'search-start', logger.onSearchStart.bind( logger ) );
	searchOverlay.on( 'search-results', logger.onSearchResults.bind( logger ) );
	searchOverlay.on( 'search-result-click', logger.onSearchResultClick.bind( logger ) );
};

module.exports = MobileWebSearchLogger;
var
	mfExtend = require( '../mfExtend' ),
	Overlay = require( '../Overlay' ),
	util = require( '../util' ),
	icons = require( '../icons' ),
	formHeader = require( '../headers' ).formHeader,
	Icon = require( '../Icon' ),
	SearchResultsView = require( './SearchResultsView' ),
	WatchstarPageList = require( '../watchstar/WatchstarPageList' ),
	SEARCH_DELAY = 300,
	SEARCH_SPINNER_DELAY = 2000;

/**
 * Overlay displaying search results
 * @class SearchOverlay
 * @extends Overlay
 * @uses SearchGateway
 * @uses Icon
 *
 * @param {Object} params Configuration options
 * @param {string} params.placeholderMsg Search input placeholder text.
 * @param {string} [params.action] of form defaults to the value of wgScript
 * @param {SearchGateway} [params.gateway]
 * @fires SearchOverlay#search-show
 * @fires SearchOverlay#search-start
 * @fires SearchOverlay#search-results
 * @fires SearchOverlay#search-result-click
 */
function SearchOverlay( params ) {
	var options = util.extend(
		true,
		{
			headerChrome: true,
			isBorderBox: false,
			className: 'overlay search-overlay',
			headers: [
				formHeader(
					// Note: Do not put the clear button inside the form
					// as hitting enter on the input element triggers a button click,
					// rather than submitting the form (see T136243)
					util.template( `<div class="overlay-title">
<form method="get" action="{{action}}" class="search-box">
	<input class="search" type="search" name="search" autocomplete="off" placeholder="{{placeholderMsg}}" aria-label="{{placeholderMsg}}" value="{{searchTerm}}">
</form>
</div>
					` ).render( {
						placeholderMsg: params.placeholderMsg,
						action: params.action || mw.config.get( 'wgScript' )
					} ),
					[
						icons.cancel()
					],
					false
				)
			],
			events: {
				'input input': 'onInputInput',
				'click .clear': 'onClickClear',
				'click .search-content': 'onClickSearchContent',
				'click .overlay-content': 'onClickOverlayContent',
				'click .overlay-content > div': 'onClickOverlayContentDiv',
				'touchstart .results': 'hideKeyboardOnScroll',
				'mousedown .results': 'hideKeyboardOnScroll',
				'click .results a': 'onClickResult'
			}
		},
		params
	);

	Overlay.call( this, options );

	this.api = options.api;
	// eslint-disable-next-line new-cap
	this.gateway = options.gateway || new options.gatewayClass( this.api );

	this.router = options.router;
}

mfExtend( SearchOverlay, Overlay, {
	/**
	 * @memberof SearchOverlay
	 * @instance
	 * @mixes Overlay#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {SearchGateway} defaults.gatewayClass The class to use to setup an API gateway.
	 *  FIXME: Should be removed when wikidata descriptions in stable (T101719)
	 * @property {Router} defaults.router instance
	 * @property {Icon} defaults.clearIcon options for the button that clears the search text.
	 * @property {string} defaults.searchTerm Search text.
	 * @property {string} defaults.clearMsg Tooltip for clear button that appears when you type
	 * into search box.
	 */
	defaults: util.extend( {}, Overlay.prototype.defaults, {
		clearIcon: new Icon( {
			tagName: 'button',
			name: 'search-clear',
			isSmall: true,
			label: mw.msg( 'mobile-frontend-clear-search' ),
			additionalClassNames: 'clear'
		} ),
		searchTerm: ''
	} ),

	/**
	 * Make sure search header is docked to the top of the screen when the
	 * user begins typing so that there is adequate space for search results
	 * above the keyboard. (This is only a potential issue when sitenotices
	 * are displayed.)
	 * @memberof SearchOverlay
	 * @instance
	 */
	onInputInput: function () {
		this.performSearch();
		this.$clear.toggle( this.$input.val() !== '' );
	},

	/**
	 * Initialize the button that clears the search field
	 * @memberof SearchOverlay
	 * @instance
	 * @return {boolean} False to cancel the native event
	 */
	onClickClear: function () {
		this.$input.val( '' ).trigger( 'focus' );
		this.performSearch();
		this.$clear.hide();
		// In beta the clear button is on top of the search input.
		// Stop propagation so that the input doesn't receive the click.
		return false;
	},

	/**
