/**
 * @class SearchResultsView
 * @extends View
 * @param {Object} options
 * @param {string} options.searchContentLabel actionable label to tell the user they can "search
 *  within pages rather than doing a full text search
 * @param {string} options.noResultsMsg shows when no results displayed
 * @param {string} options.searchContentNoResultsMsg alternative to options.searchContentLabel that
 * shows when no search results have been displayed.
 */
class SearchResultsView extends View {
	/** @inheritdoc */
	get isTemplateMode() {
		return true;
	}
	/** @inheritdoc */
	get template() {
		return util.template( `
<div class="search-results-view">
	<div class="search-content overlay-header search-results-view">
		<ul>
			<li>{{! search content icon goes here }}</li>
		</ul>
		<div class="caption">
			<p class="with-results">{{searchContentLabel}}</p>
			<p class="without-results">{{noResultsMsg}}</p>
			<p class="without-results">{{{searchContentNoResultsMsg}}}</p>
		</div>
	</div>
	<div class="spinner-container position-fixed"></div>
	<div class="results">
		<div class="results-list-container"></div>
		{{#feedback}}
			<div class="search-feedback">
				{{prompt}}
			</div>
		{{/feedback}}
	</div>
</div>`
		);
	}
	/** @inheritdoc */
	preRender() {
		if ( feedbackLink ) {
			this.options.feedback = {
				prompt: mw.msg( 'mobile-frontend-search-feedback-prompt' ) };
		}
	}
	/** @inheritdoc */
	postRender( options ) {
		super.postRender( options );
		this.$el.find( '.search-content li' ).append(
			new Icon( {
				tagName: 'a',
				// When this icon is clicked we want to reset the hash for subsequent views
				href: '#',
				name: 'search-content',
				label: mw.msg( 'mobile-frontend-search-content' )
			} ).$el
		);
		this.$el.find( '.spinner-container' ).append( spinner );
		if ( feedbackLink ) {
			this.$el.find( '.search-feedback' ).append(
				new Anchor( {
					label: mw.msg( 'mobile-frontend-search-feedback-link-text' ),
					href: feedbackLink
				} ).$el
			);
		}
	}
}

module.exports = SearchResultsView;
var context = require( '../context' );

function subscribeMobileWebSearchSchema() {
	mw.loader.using( [
		'ext.eventLogging'
	] ).then( function () {
		var // Schema class provided by ext.eventLogging
			Schema = mw.eventLog.Schema,
			/**
			 * MobileWebSearch schema
			 * https://meta.wikimedia.org/wiki/Schema:MobileWebSearch
			 */
			schemaMobileWebSearch = new Schema(
				'MobileWebSearch',
				// todo: use a default value of 0 once config lands in production.
				mw.config.get( 'wgMFSchemaSearchSampleRate', 1 / 1000 ),
				/**
				 * @property {Object} defaults Default options hash.
				 * @property {string} defaults.platform Always "mobileweb"
				 * @property {string} defaults.platformVersion The version of MobileFrontend
				 *  that the user is using. One of "stable" or "beta"
				 */
				{
					platform: 'mobileweb',
					platformVersion: context.getMode()
				}
			);

		mw.trackSubscribe( 'mf.schemaMobileWebSearch', function ( topic, data ) {
			schemaMobileWebSearch.log( data );
		} );
	} );
}

module.exports = {
	subscribeMobileWebSearchSchema: subscribeMobileWebSearchSchema
};
