var browser = require( './Browser' ).getSingleton(),
	util = require( './util' ),
	escapeHash = util.escapeHash,
	arrowOptions = {
		name: 'arrow',
		additionalClassNames: 'indicator'
	},
	Icon = require( './Icon' );

/**
 *
 * @typedef {Object} ToggledEvent
 * @prop {boolean} expanded True if section is opened, false if closed.
 * @prop {Page} page
 * @prop {boolean} isReferenceSection
 * @prop {JQuery.Object} $heading
 */

/**
 * A class for enabling toggling
 *
 * @class Toggler
 * @param {Object} options
 * @param {OO.EventEmitter} options.eventBus Object used to emit section-toggled events.
 * @param {jQuery.Object} options.$container to apply toggling to
 * @param {string} options.prefix a prefix to use for the id.
 * @param {Page} options.page to allow storage of session for future visits
 * @param {boolean} [options.isClosed]
 */
function Toggler( options ) {
	this.eventBus = options.eventBus;
	this._enable( options.$container, options.prefix, options.page, options.isClosed );
}

/**
 * Using the settings module looks at what sections were previously expanded on
 * existing page.
 *
 * @param {Page} page
 * @return {Object} representing open sections
 */
function getExpandedSections( page ) {
	var expandedSections = JSON.parse( mw.storage.get( 'expandedSections' ) || '{}' );
	expandedSections[page.title] = expandedSections[page.title] || {};
	return expandedSections;
}

/**
* @param {Object} expandedSections
* Save expandedSections to localStorage
*/
function saveExpandedSections( expandedSections ) {
	mw.storage.set(
		'expandedSections', JSON.stringify( expandedSections )
	);
}

/**
 * Given an expanded heading, store it to localStorage.
 * If the heading is collapsed, remove it from localStorage.
 *
 * @param {jQuery.Object} $heading - A heading belonging to a section
 * @param {Page} page
 */
function storeSectionToggleState( $heading, page ) {
	var headline = $heading.find( 'span' ).attr( 'id' ),
		isSectionOpen = $heading.hasClass( 'open-block' ),
		expandedSections = getExpandedSections( page );

	if ( headline ) {
		if ( isSectionOpen ) {
			expandedSections[page.title][headline] = ( new Date() ).getTime();
		} else {
			delete expandedSections[page.title][headline];
		}

		saveExpandedSections( expandedSections );
	}
}

/**
 * Expand sections that were previously expanded before leaving this page.
 * @param {Toggler} toggler
 * @param {jQuery.Object} $container
 * @param {Page} page
 */
function expandStoredSections( toggler, $container, page ) {
	var $sectionHeading, $headline,
		expandedSections = getExpandedSections( page ),
		$headlines = $container.find( '.section-heading span' );

	$headlines.each( function () {
		$headline = $container.find( this );
		$sectionHeading = $headline.parents( '.section-heading' );
		// toggle only if the section is not already expanded
		if (
			expandedSections[page.title][$headline.attr( 'id' )] &&
		!$sectionHeading.hasClass( 'open-block' )
		) {
			toggler.toggle( $sectionHeading, page );
		}
	} );
}

/**
 * Clean obsolete (saved more than a day ago) expanded sections from
 * localStorage.
 * @param {Page} page
 */
function cleanObsoleteStoredSections( page ) {
	var now = ( new Date() ).getTime(),
		expandedSections = getExpandedSections( page ),
		// the number of days between now and the time a setting was saved
		daysDifference;
	Object.keys( expandedSections ).forEach( function ( page ) {
		var sections = expandedSections[ page ];
		// clean the setting if it is more than a day old
		Object.keys( sections ).forEach( function ( section ) {
			var timestamp = sections[ section ];
			daysDifference = Math.floor( ( now - timestamp ) / 1000 / 60 / 60 / 24 );
			if ( daysDifference >= 1 ) {
				delete expandedSections[page][section];
			}
		} );
	} );
	saveExpandedSections( expandedSections );
}

/**
 * Given a heading, toggle it and any of its children
 *
 * @memberof Toggler
 * @instance
 * @param {jQuery.Object} $heading A heading belonging to a section
 * @param {Page} page
 */
Toggler.prototype.toggle = function ( $heading, page ) {
	var indicator,
		wasExpanded = $heading.is( '.open-block' ),
		$content = $heading.next();

	$heading.toggleClass( 'open-block' );
	$heading.data( 'indicator' ).remove();

	arrowOptions.rotation = wasExpanded ? 0 : 180;
	indicator = new Icon( arrowOptions ).prependTo( $heading );
	$heading.data( 'indicator', indicator );

	$content
		.toggleClass( 'open-block' )
		.attr( {
			'aria-pressed': !wasExpanded,
			'aria-expanded': !wasExpanded
		} );

	/**
	 * Global event emitted after a section has been toggled
	 * @event section-toggled
	 * @type {ToggledEvent}
	 */
	this.eventBus.emit( 'section-toggled', {
		expanded: wasExpanded,
		isReferenceSection: Boolean( $content.attr( 'data-is-reference-section' ) ),
		$heading: $heading
	} );

	if ( !browser.isWideScreen() ) {
		storeSectionToggleState( $heading, page );
	}
};

/**
 * Enables toggling via enter and space keys
 *
 * @param {Toggler} toggler instance.
 * @param {jQuery.Object} $heading
 * @param {Page} page
 */
function enableKeyboardActions( toggler, $heading, page ) {
	$heading.on( 'keypress', function ( ev ) {
		if ( ev.which === 13 || ev.which === 32 ) {
			// Only handle keypresses on the "Enter" or "Space" keys
			toggler.toggle( $heading, page );
		}
	} ).find( 'a' ).on( 'keypress mouseup', function ( ev ) {
		ev.stopPropagation();
	} );
}

/**
 * Reveals an element and its parent section as identified by it's id
 *
 * @memberof Toggler
 * @instance
 * @param {string} selector A css selector that identifies a single element
 * @param {Object} $container jQuery element to search in
 * @param {Page} page
 */
Toggler.prototype.reveal = function ( selector, $container, page ) {
	var $target, $heading;

	// jQuery will throw for hashes containing certain characters which can break toggling
	try {
		$target = $container.find( escapeHash( selector ) );
		$heading = $target.parents( '.collapsible-heading' );
		// The heading is not a section heading, check if in a content block!
		if ( !$heading.length ) {
			$heading = $target.parents( '.collapsible-block' ).prev( '.collapsible-heading' );
		}
		if ( $heading.length && !$heading.hasClass( 'open-block' ) ) {
			this.toggle( $heading, page );
		}
		if ( $heading.length ) {
			// scroll again after opening section (opening section makes the page longer)
			window.scrollTo( 0, $target.offset().top );
		}
	} catch ( e ) {}
};

/**
 * Enables section toggling in a given container when wgMFCollapseSectionsByDefault
 * is enabled.
 *
 * @memberof Toggler
 * @instance
 * @param {jQuery.Object} $container to apply toggling to
 * @param {string} prefix a prefix to use for the id.
 * @param {Page} page to allow storage of session for future visits
 * @param {boolean} [isClosed] whether the element should begin closed
 * @private
 */
Toggler.prototype._enable = function ( $container, prefix, page, isClosed ) {
	var tagName, expandSections, indicator, $content,
		$firstHeading,
		$link,
		self = this,
		collapseSectionsByDefault = mw.config.get( 'wgMFCollapseSectionsByDefault' );

	// Also allow .section-heading if some extensions like Wikibase
	// want to toggle other headlines than direct descendants of $container.
	$firstHeading = $container.find( '> h1,> h2,> h3,> h4,> h5,> h6,.section-heading' ).eq( 0 );
	tagName = $firstHeading.prop( 'tagName' ) || 'H1';

	if ( collapseSectionsByDefault === undefined ) {
		// Old default behavior if on cached output
		collapseSectionsByDefault = true;
	}
	expandSections = !collapseSectionsByDefault || mw.storage.get( 'expandSections' ) === 'true';

	$container.children( tagName ).each( function ( i ) {
		var isReferenceSection,
			$heading = $container.find( this ),
			$indicator = $heading.find( '.indicator' ),
			id = prefix + 'collapsible-block-' + i;
		// Be sure there is a div wrapping the section content.
		// Otherwise, collapsible sections for this page is not enabled.
		if ( $heading.next().is( 'div' ) ) {
			$content = $heading.next( 'div' );
			isReferenceSection = Boolean( $content.attr( 'data-is-reference-section' ) );
			$heading
				.addClass( 'collapsible-heading ' )
				.data( 'section-number', i )
				.attr( {
					tabindex: 0,
					'aria-haspopup': 'true',
					'aria-controls': id
				} )
				.on( 'click', function ( ev ) {
					// don't toggle, if the click target was a link
					// (a link in a section heading)
					// See T117880
					if ( !ev.target.href ) {
						// prevent taps/clicks on edit button after toggling (bug 56209)
						ev.preventDefault();
						self.toggle( $heading, page );
					}
				} );

			arrowOptions.rotation = expandSections ? 180 : 0;
			indicator = new Icon( arrowOptions );
			if ( $indicator.length ) {
				// replace the existing indicator
				$indicator.replaceWith( indicator.$el );
			} else {
				indicator.prependTo( $heading );
			}
			$heading.data( 'indicator', indicator.$el );
			$content
				.addClass( 'collapsible-block' )
				.eq( 0 )
				.attr( {
					// We need to give each content block a unique id as that's
					// the only way we can tell screen readers what element we're
					// referring to (aria-controls)
					id: id,
					'aria-pressed': 'false',
					'aria-expanded': 'false'
				} );

			enableKeyboardActions( self, $heading, page );
			if (
				!isReferenceSection && (
					!isClosed && browser.isWideScreen() || expandSections
				)
			) {
				// Expand sections by default on wide screen devices
				// or if the expand sections setting is set.
				// The wide screen logic for determining whether to collapse sections initially
				// should be kept in sync with mobileoptions#initLocalStorageElements().
				self.toggle( $heading, page );
			}
		}
	} );

	/* eslint-disable no-restricted-properties */
	/**
	 * Checks the existing hash and toggles open any section that contains the fragment.
	 *
	 * @method
	 */
	function checkHash() {
		var hash = window.location.hash;
		if ( hash.indexOf( '#' ) === 0 ) {
			// Non-latin characters in the hash will be provided percent-encoded, which
			// jQuery would later fail to cope with.
			self.reveal( decodeURIComponent( hash ), $container, page );
		}
	}

	/**
	 * Checks the value of wgInternalRedirectTargetUrl and reveals the collapsed
	 * section that contains it if present
	 *
	 * @method
	 */
	function checkInternalRedirectAndHash() {
		var internalRedirect = mw.config.get( 'wgInternalRedirectTargetUrl' ),
			internalRedirectHash = internalRedirect ? internalRedirect.split( '#' )[1] : false;

		if ( internalRedirectHash ) {
			window.location.hash = internalRedirectHash;
			self.reveal( internalRedirectHash, $container, page );
		}
	}
	/* eslint-enable no-restricted-properties */

	checkInternalRedirectAndHash();
	checkHash();
	// Restricted to links created by editors and thus outside our control
	// T166544 - don't do this for reference links - they will be handled elsewhere
	$link = $container.find( 'a:not(.reference a)' );
	$link.on( 'click', function () {
		// the link might be an internal link with a hash.
		// if it is check if we need to reveal any sections.
		if ( $link.attr( 'href' ) !== undefined &&
		$link.attr( 'href' ).indexOf( '#' ) > -1
		) {
			checkHash();
		}
	} );
	util.getWindow().on( 'hashchange', function () {
		checkHash();
	} );

	if ( !browser.isWideScreen() && page ) {
		expandStoredSections( this, $container, page );
		cleanObsoleteStoredSections( page );
	}
};

Toggler._getExpandedSections = getExpandedSections;
Toggler._expandStoredSections = expandStoredSections;
Toggler._cleanObsoleteStoredSections = cleanObsoleteStoredSections;

module.exports = Toggler;
/**
 * Return the language code of the device in lowercase
 *
 * @ignore
 * @param {window.navigator} navigator
 * @return {string|undefined}
 */
function getDeviceLanguage( navigator ) {
	const lang = navigator.languages ?
		navigator.languages[ 0 ] :
		navigator.language || navigator.userLanguage ||
				navigator.browserLanguage || navigator.systemLanguage;

	return lang ? lang.toLowerCase() : undefined;
}

module.exports = getDeviceLanguage;
const
	m = require( '../moduleLoaderSingleton' ),
	getDeviceLanguage = require( './getDeviceLanguage' ),
	Overlay = require( '../Overlay' ),
	promisedView = require( '../promisedView' );

/**
 * @ignore
 * @param {PageGateway} gateway
 * @return {jQuery.Promise} Resolves to LanguageSearcher
 */
function loadLanguageSearcher( gateway ) {
	return mw.loader.using( 'mobile.languages.structured' ).then( function () {
		return gateway.getPageLanguages( mw.config.get( 'wgPageName' ), mw.config.get( 'wgUserLanguage' ) );
	} ).then( function ( data ) {
		const LanguageSearcher = m.require( 'mobile.languages.structured/LanguageSearcher' );

		return new LanguageSearcher( {
			languages: data.languages,
			variants: data.variants,
			deviceLanguage: getDeviceLanguage( navigator )
		} );

	} );
}

/**
 * Factory function that returns a language featured instance of an Overlay
 *
 * @param {PageGateway} pageGateway
 * @return {Overlay}
 */
function languageOverlay( pageGateway ) {
	return Overlay.make(
		{
			heading: mw.msg( 'mobile-frontend-language-heading' ),
			className: 'overlay language-overlay'
		}, promisedView( loadLanguageSearcher( pageGateway ) )
	);
}

// To make knowing when async logic has resolved easier in tests
languageOverlay.test = {
	loadLanguageSearcher: loadLanguageSearcher
};

module.exports = languageOverlay;
var m = require( '../moduleLoaderSingleton' ),
	promisedView = require( '../promisedView' ),
	util = require( '../util' ),
	header = require( '../headers' ).header,
	icons = require( '../icons' ),
	Overlay = require( '../Overlay' );

/**
 * Produce an overlay for mediaViewer
 * @param {Object} options
 * @return {Overlay}
 */
function mediaViewerOverlay( options ) {
	var overlay = Overlay.make(
		{
			headers: [
				header( '', [], icons.cancel( 'gray' ) )
			],
			className: 'overlay media-viewer'
		},
		promisedView(
			util.Promise.all( [
				mw.loader.using( 'mobile.mediaViewer' )
			] ).then( function () {
				var ImageCarousel = m.require( 'mobile.mediaViewer' ).ImageCarousel;
				return new ImageCarousel( options );
			} )
		)
	);

	return overlay;
}

module.exports = mediaViewerOverlay;
var
	moduleLoader = require( './moduleLoaderSingleton' ),
	schemaMobileWebSearch = require( './search/schemaMobileWebSearch' );

// Expose the entry chunk through libraryTarget and library. This allows
// arbitrary file access via ResourceLoader like
// `mfModules['mobile.startup'].moduleLoader.require('mobile.startup/LoadingOverlay')`.
module.exports = {
	moduleLoader: moduleLoader,
	mfExtend: require( './mfExtend' ),
	context: require( './context' ),
	time: require( './time' ),
	util: require( './util' ),
	View: require( './View' ),
	PageGateway: require( './PageGateway' ),
	Browser: require( './Browser' ),
	Button: require( './Button' ),
	Icon: require( './Icon' ),
	ReferencesDrawer: require( './references/ReferencesDrawer' ),
	ReferencesGateway: require( './references/ReferencesGateway' ),
	ReferencesHtmlScraperGateway: require( './references/ReferencesHtmlScraperGateway' ),
	ReferencesMobileViewGateway: require( './references/ReferencesMobileViewGateway' ),
	icons: require( './icons' ),
	Page: require( './Page' ),
	currentPage: require( './currentPage' ),
	PageHTMLParser: require( './PageHTMLParser' ),
	currentPageHTMLParser: require( './currentPageHTMLParser' ),
	Anchor: require( './Anchor' ),
	Skin: require( './Skin' ),
	OverlayManager: require( './OverlayManager' ),
	Overlay: require( './Overlay' ),
	loadingOverlay: require( './loadingOverlay' ),
	CtaDrawer: require( './CtaDrawer' ),
	toast: require( './toast' ),
	Watchstar: require( './watchstar/Watchstar' ),
	rlModuleLoader: require( './rlModuleLoader' ),
	eventBusSingleton: require( './eventBusSingleton' ),
	Toggler: require( './Toggler' ),
	toc: {
		TableOfContents: require( './toc/TableOfContents' )
	},
	notifications: {
		overlay: require( './notifications/overlay' )
	},
	search: {
		SearchOverlay: require( './search/SearchOverlay' ),
		MobileWebSearchLogger: require( './search/MobileWebSearchLogger' ),
		SearchGateway: require( './search/SearchGateway' )
	},
	lazyImages: {
		lazyImageLoader: require( './lazyImages/lazyImageLoader' )
	},
	talk: {
		overlay: require( './talk/overlay' )
	},
	languageOverlay: require( './languageOverlay/languageOverlay' ),
	mediaViewer: {
		overlay: require( './mediaViewer/overlay' )
	}
};

mw.mobileFrontend = moduleLoader;

// Setup a single export for new modules to fold all of the above lines into.
// One export to rule them all!
moduleLoader.define( 'mobile.startup', module.exports );

schemaMobileWebSearch.subscribeMobileWebSearchSchema();
var Overlay = require( '../Overlay' ),
	m = require( '../moduleLoaderSingleton' ),
	promisedView = require( '../promisedView' ),
	View = require( '../View' ),
	Anchor = require( '../Anchor' );

/**
 * This callback is displayed as a global member.
 * @callback countChangeCallback
 * @param {number} count a capped (0-99 or 99+) count
 */

/**
 * Make a notification overlay
 *
 * @param {countChangeCallback} onCountChange receives one parameter - a capped (0-99 or 99+) count.
 * @param {Function} onNotificationListRendered a function that is called when the
 *   notifications list has fully rendered (taking no arguments)
 * @return {Overlay}
 */
function notificationsOverlay( onCountChange, onNotificationListRendered ) {
	var markAllReadButton,
		oouiPromise = mw.loader.using( 'mobile.notifications.overlay' ).then( function () {
			markAllReadButton = new OO.ui.ButtonWidget( {
				icon: 'checkAll',
				title: mw.msg( 'echo-mark-all-as-read' )
			} );
			return View.make(
				{ class: 'notifications-overlay-header-markAllRead' },
				[ markAllReadButton.$element ]
			);
		} ),
		markAllReadButtonView = promisedView( oouiPromise );
	// hide the button spinner as it is confusing to see in the top right corner
	markAllReadButtonView.$el.hide();

	return Overlay.make(
		{
			heading: '<strong>' + mw.message( 'notifications' ).escaped() + '</strong>',
			footerAnchor: new Anchor( {
				href: mw.util.getUrl( 'Special:Notifications' ),
				progressive: true,
				additionalClassNames: 'footer-link notifications-archive-link',
				label: mw.msg( 'echo-overlay-link' )
			} ).options,
			headerActions: [ markAllReadButtonView ],
			isBorderBox: false,
			className: 'overlay notifications-overlay navigation-drawer'
		},
		promisedView(
			oouiPromise.then( function () {
				var list = m.require( 'mobile.notifications.overlay' ).list;
				return list( mw.echo, markAllReadButton, onCountChange,
					onNotificationListRendered );
			} )
		)
	);
}

module.exports = notificationsOverlay;
var Drawer = require( './../Drawer' ),
	util = require( './../util' ),
	icons = require( './../icons' ),
	mfExtend = require( './../mfExtend' ),
	ReferencesGateway = require( './ReferencesGateway' ),
	Icon = require( './../Icon' );

/**
 * Drawer for references
 * @class ReferencesDrawer
 * @extends Drawer
 * @uses Icon
 * @param {Object} props
 */
function ReferencesDrawer( props ) {
	Drawer.call(
		this,
		util.extend(
			{
				className: 'drawer position-fixed text references-drawer',
				events: { 'click sup a': 'showNestedReference' }
			},
			props
		)
	);
}

mfExtend( ReferencesDrawer, Drawer, {
	/**
	 * @memberof ReferencesDrawer
	 * @instance
	 * @mixes Drawer#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {boolean} defaults.error whether an error message is being shown
	 */
	defaults: util.extend( {}, Drawer.prototype.defaults, {
		errorClassName: new Icon( {
			name: 'error',
			hasText: true,
			isSmall: true
		} ).getClassName()
	} ),
	/**
	 * @inheritdoc
	 * @memberof ReferencesDrawer
	 * @instance
	 */
	show: function () {
		return Drawer.prototype.show.apply( this, arguments );
	},
	/**
	 * @memberof ReferencesDrawer
	 * @instance
	 */
	template: util.template( `
<div class="references-drawer__header"></div>
{{#error}}
	<div class="{{errorClassName}}">
{{/error}}
<sup>{{title}}</sup>
{{#text}}
	{{{text}}}
{{/text}}
{{#error}}</div>{{/error}}
	` ),
	/**
	 * @inheritdoc
	 * @memberof ReferencesDrawer
	 * @instance
	 */
	closeOnScroll: false,
	/**
	 * @inheritdoc
	 * @memberof ReferencesDrawer
	 * @instance
	 */
	postRender: function () {
		var windowHeight = util.getWindow().height();

		Drawer.prototype.postRender.apply( this );
		this.$el.find( '.references-drawer__header' ).append( [
			new Icon( {
				isSmall: true,
				name: 'citation-invert',
				additionalClassNames: 'references-drawer__title',
				hasText: true,
				label: mw.msg( 'mobile-frontend-references-citation' )
			} ).$el,
			icons.cancel( 'gray' ).$el
		] );
		// For lazy loading references - if no text append a spinner
		if ( !this.options.text ) {
			this.$el.append( icons.spinner().$el );
		}

		// make sure the drawer doesn't take up more than 50% of the viewport height
		if ( windowHeight / 2 < 400 ) {
			this.$el.css( 'max-height', windowHeight / 2 );
		}

		this.on( 'show', this.onShow.bind( this ) );
		this.on( 'hide', this.onHide.bind( this ) );
	},
	/**
	 * Make body not scrollable
	 * @memberof ReferencesDrawer
	 * @instance
	 */
	onShow: function () {
		util.getDocument().find( 'body' ).addClass( 'drawer-enabled' );
	},
	/**
	 * Restore body scroll
	 * @memberof ReferencesDrawer
	 * @instance
	 */
	onHide: function () {
		util.getDocument().find( 'body' ).removeClass( 'drawer-enabled' );
	},
	/**
	 * Fetch and render nested reference upon click
	 * @memberof ReferencesDrawer
	 * @instance
	 * @param {string} id of the reference to be retrieved
	 * @param {Page} page to locate reference for
	 * @param {string} refNumber the number it identifies as in the page
	 * @param {PageHTMLParser} pageHTMLParser
	 * @return {jQuery.Deferred}
	 */
	showReference: function ( id, page, refNumber, pageHTMLParser ) {
		var drawer = this,
			gateway = this.options.gateway;

		// Save the page in case we have to show a nested reference.
		this.options.page = page;
		this.options.pageHTMLParser = pageHTMLParser;
		// If API is being used we want to show the drawer with the spinner while query runs
		drawer.show();
		return gateway.getReference( id, page, pageHTMLParser ).then( function ( reference ) {
			drawer.render( {
				title: refNumber,
				text: reference.text
			} );
		}, function ( err ) {
			if ( err === ReferencesGateway.ERROR_NOT_EXIST ) {
				drawer.hide();
			} else {
				drawer.render( {
					error: true,
					title: refNumber,
					text: mw.msg( 'mobile-frontend-references-citation-error' )
				} );
			}
		} );
	},
	/**
	 * Fetch and render nested reference upon click
	 * @memberof ReferencesDrawer
	 * @instance
	 * @param {jQuery.Event} ev
	 * @return {boolean} False to cancel the native event
	 */
	showNestedReference: function ( ev ) {
		var $dest = this.$el.find( ev.target );

		this.showReference( $dest.attr( 'href' ), this.options.page, $dest.text(), this.options.pageHTMLParser );
		// Don't hide the already shown drawer via propagation
		// and stop default scroll behaviour.
