/* global $ */
var util,
	log = mw.log; // resource-modules-disable-line

/**
 * Utility library
 * @class util
 * @singleton
 */
util = {
	/**
	 * Escape a string for use as a css selector
	 * @memberof util
	 * @instance
	 * @param {string} selector
	 * @return {string}
	 */
	escapeSelector: function ( selector ) {
		return $.escapeSelector( selector );
	},
	/**
	 * Wrapper class for the $.grep
	 * @memberof util
	 * @instance
	 * @return {jQuery.Deferred}
	 */
	grep: function () {
		return $.grep.apply( $, arguments );
	},
	/**
	 * Run method when document is ready.
	 * @memberof util
	 * @instance
	 * @param {Function} fn
	 * @return {jQuery.Object}
	 */
	docReady: function ( fn ) {
		return $( fn );
	},
	/**
	 * Wrapper class for the $.when
	 * @memberof util
	 * @instance
	 * @return {jQuery.Deferred}
	 */
	when: function () {
		return $.when.apply( $, arguments );
	},
	/**
	 * Wrapper class for the Deferred method
	 * @memberof util
	 * @instance
	 * @return {jQuery.Deferred}
	 */
	Deferred: function () {
		var d = $.Deferred(),
			warning = 'Use Promise compatible methods `then` and `catch` instead.';

		/* eslint-disable no-restricted-properties */
		log.deprecate( d, 'fail', d.fail, warning );
		log.deprecate( d, 'always', d.always, warning );
		log.deprecate( d, 'done', d.done, warning );
		/* eslint-enable no-restricted-properties */
		return d;
	},
	/**
	 * Adds a class to the document
	 * @memberof util
	 * @instance
	 * @return {jQuery.Object} element representing the documentElement
	 */
	getDocument: function () {
		return $( document.documentElement );
	},
	/**
	 * Get the window object
	 * @memberof util
	 * @instance
	 * @return {jQuery.Object}
	 */
	getWindow: function () {
		return $( window );
	},
	/**
	 * Given some html, create new element(s).
	 * Unlike jQuery.parseHTML this will return a jQuery object
	 * not an array.
	 * @memberof util
	 * @instance
	 * @param {string} html
	 * @param {Element} [ctx] Document element to serve as the context in which the HTML fragment will be created
	 * @return {jQuery.Object}
	 */
	parseHTML: function ( html, ctx ) {
		return $( $.parseHTML( html, ctx ) );
	},
	/**
	 * wrapper for jQuery util noop function
	 * @memberof util
	 * @instance
	 * @return {Function}
	 */
	noop: $.noop,
	/**
	 * wrapper for jQuery util function to check if something is numeric
	 * @memberof util
	 * @instance
	 * @return {boolean}
	 */
	isNumeric: function () {
		return $.isNumeric.apply( $, arguments );
	},
	/**
	 * Wrapper for jQuery.extend method. In future this can be bound to Object.assign
	 * when support allows.
	 * @memberof util
	 * @instance
	 * @return {Object}
	 */
	extend: function () {
		return $.extend.apply( $, arguments );
	},
	/**
	 * Escape dots and colons in a hash, jQuery doesn't like them because they
	 * look like CSS classes and pseudoclasses. See
	 * http://bugs.jquery.com/ticket/5241
	 * http://stackoverflow.com/questions/350292/how-do-i-get-jquery-to-select-elements-with-a-period-in-their-id
	 * @memberof util
	 * @instance
	 * @param {string} hash A hash to escape
	 * @return {string}
	 */
	escapeHash: function ( hash ) {
		return hash.replace( /(:|\.)/g, '\\$1' );
	},

	/**
	 * Heuristic for determining whether an Event should be handled by
	 * MobileFrontend or allowed to bubble to the browser.
	 * @memberof util
	 * @instance
	 * @param {Event} ev
	 * @return {boolean} True if event is modified with control, alt, meta, or
	 *                   shift keys and should probably be handled by the
	 *                   browser.
	 *
	 * todo: move this function to a ClickUtil file once bundling and code
	 * splitting is supported.
	 */
	isModifiedEvent: function ( ev ) {
		return ev.altKey || ev.ctrlKey || ev.metaKey || ev.shiftKey;
	},

	/**
	 * Pipe event emitted by source through proxy. Subscribers to proxy will receive the event as
	 * though proxy was the originator.
	 *
	 * @param {OO.EventEmitter} src
	 * @param {OO.EventEmitter} proxy
	 * @param {string} event Event type to listen for.
	 * @param {any[]} [args] Arguments to pass to subscribers, will be prepended to emitted arguments.
	 * @return {OO.EventEmitter} The source.
	 */
	repeatEvent: function ( src, proxy, event, args ) {
		return src.on( event, function ( args ) { return proxy.emit( event, args ); }, args );
	}
};

module.exports = util;
var browser,
	$html = require( './util' ).getDocument();

/**
 * Memoize a class method. Caches the result of the method based on the
 * arguments. Instances do not share a cache.
 * @param {Function} method Method to be memoized
 * @return {Function}
 */
function memoize( method ) {
	/**
	 * Memoized version of the method
	 * @return {Function}
	 */
	var memoized = function () {
		var cache = this[ '__cache' + memoized.cacheId ] ||
			( this[ '__cache' + memoized.cacheId ] = {} ),
			key = [].join.call( arguments, '|' );
		if ( Object.prototype.hasOwnProperty.call( cache, key ) ) {
			return cache[ key ];
		}
		return ( cache[ key ] = method.apply( this, arguments ) );
	};
	memoized.cacheId = Date.now().toString() + Math.random().toString();
	return memoized;
}

/**
 * Representation of user's current browser
 * @class Browser
 * @param {string} ua the user agent of the current browser
 * @param {jQuery.Object} $container an element to associate with the Browser object
 */
function Browser( ua, $container ) {
	this.userAgent = ua;
	this.$el = $container;
	this._fixIosLandscapeBug();
}

Browser.prototype = {
	/**
	 * When rotating to landscape stop page zooming on ios 4 and 5.
	 * @memberof Browser
	 * @instance
	 * @private
	 */
	_fixIosLandscapeBug: function () {
		var self = this,
			viewport = this.$el.find( 'meta[name="viewport"]' )[0];

		// see http://adactio.com/journal/4470/ (fixed in ios 6)
		if ( viewport && ( this.isIos( 4 ) || this.isIos( 5 ) ) ) {
			this.lockViewport();
			document.addEventListener( 'gesturestart', function () {
				self.lockViewport();
			}, false );
		}
	},
	/**
	 * Returns whether the current browser is an ios device.
	 * FIXME: jquery.client does not support iPad detection so we cannot use it.
	 * @memberof Browser
	 * @instance
	 * @param {number} [version] integer describing a specific version you want to test against.
	 * @return {boolean}
	 */
	isIos: memoize( function ( version ) {
		var ua = this.userAgent,
			ios = /ipad|iphone|ipod/i.test( ua );

		if ( ios && version ) {
			switch ( version ) {
				case 8:
					// Test UA for iOS8. Or for simulator look for Version 8
					// In the iOS simulator the OS is the host machine OS version
					// This makes testing in iOS8 simulator work as expected
					return /OS 8_/.test( ua ) || /Version\/8/.test( ua );
				case 4:
					return /OS 4_/.test( ua );
				case 5:
					return /OS 5_/.test( ua );
				default:
					return false;
			}
		} else {
			return ios;
		}
	} ),
	/**
	 * Locks the viewport so that pinch zooming is disabled
	 * @memberof Browser
	 * @instance
	 */
	lockViewport: function () {
		if ( this.$el ) {
			this.$el.find( 'meta[name="viewport"]' )
				.attr( 'content', 'initial-scale=1.0, maximum-scale=1.0, user-scalable=no' );
		}
	},
	/**
	 * Determine if a device has a widescreen.
	 * @memberof Browser
	 * @instance
	 * @return {boolean}
	 */
	isWideScreen: memoize( function () {
		var val = parseInt( mw.config.get( 'wgMFDeviceWidthTablet' ), 10 );
		// Check portrait and landscape mode to be consistent
		return window.innerWidth >= val || window.innerHeight >= val;
	} ),
	/**
	 * Checks browser support for CSS transforms, transitions
	 * and CSS animation.
	 * Currently assumes support for the latter 2 in the case of the
	 * former.
	 * See http://stackoverflow.com/a/12621264/365238
	 * @memberof Browser
	 * @instance
	 * @return {boolean}
	 */
	supportsAnimations: memoize( function () {
		var elemStyle = document.createElement( 'foo' ).style;
		function supportsProperty( property ) {
			// We only test "webkit-", because that's the only prefix needed for the relevant
			// properties (in supportsAnimations) and supported browsers. If usage is expanded,
			// other prefixes may need to be checked as well.
			return property in elemStyle ||
				( 'webkit' + property[ 0 ].toUpperCase() + property.slice( 1 ) ) in elemStyle;
		}
		return supportsProperty( 'animationName' ) &&
			supportsProperty( 'transform' ) &&
			supportsProperty( 'transition' );
	} ),
	/**
	 * Whether touchstart and other touch events are supported by the current browser.
	 * @memberof Browser
	 * @instance
	 * @return {boolean}
	 */
	supportsTouchEvents: memoize( function () {
		return 'ontouchstart' in window;
	} ),
	/**
	 * Detect if browser supports geolocation
	 * @memberof Browser
	 * @instance
	 * @return {boolean}
	 */
	supportsGeoLocation: memoize( function () {
		return 'geolocation' in navigator;
	} )
};

/**
 * @memberof Browser
 * @return {Browser}
 */
Browser.getSingleton = function () {
	if ( !browser ) {
		browser = new Browser( window.navigator.userAgent, $html );
	}
	return browser;
};

module.exports = Browser;
/**
 * Class for managing modules
 *
 * A module in this context is essentially a Javascript class (not to be confused with
 * ResourceLoader modules).
 *
 * @class ModuleLoader
 * @extends OO.EventEmitter
 */
function ModuleLoader() {
	/**
	 * @property {Object} register of defined modules
	 * @private
	 */
	this._register = {};
	OO.EventEmitter.call( this );
}

ModuleLoader.prototype = {
	/**
	 * Require (import) a module previously defined using define().
	 * Searches core module registry using mw.loader.require before consulting
	 * its own local registry. This method is deprecated, please do not use.
	 * @memberof ModuleLoader
	 * @instance
	 * @param {string} id Required module id.
	 * @return {Object} Required module, can be any JavaScript object.
	 */
	require: function ( id ) {
		var module, args,
			registry = this._register;

		/**
		 * @return {Object} Module
		 */
		function localRequire() {
			if ( !Object.hasOwnProperty.call( registry, id ) ) {
				throw new Error( 'MobileFrontend Module not found: ' + id );
			}
			return registry[ id ];
		}
		args = id.split( '/' );
		try {
			module = mw.loader.require( args[0] );
			if ( module[ args[1] ] ) {
				return module[ args[1] ];
			} else {
				return localRequire();
			}
		} catch ( e ) {
			return localRequire();
		}
	},

	/**
	 * Define a module which can be later required (imported) using require().
	 * @memberof ModuleLoader
	 * @instance
	 * @param {string} id Defined module id.
	 * @param {Object} obj Defined module body, can be any JavaScript object.
	 * @return {Object}
	 */
	define: function ( id, obj ) {
		var self = this;

		if ( Object.hasOwnProperty.call( this._register, id ) ) {
			throw new Error( 'Module already exists: ' + id );
		}
		this._register[ id ] = obj;
		// return an object of additionally functions to do with the registered module
		return {
			/**
			 * @see ModuleLoader#deprecate
			 * @param {string} deprecatedId Defined module id, which is deprecated.
			 * @ignore
			 */
			deprecate: function ( deprecatedId ) {
				self.deprecate( deprecatedId, obj, id );
			}
		};
	},

	/**
	 * Deprecate a module and give an replacement (if there is any).
	 * @memberof ModuleLoader
	 * @instance
	 * @param {string} id Defined module id, which is deprecated.
	 * @param {Object} obj Defined module body, can be any JavaScript object.
	 * @param {string} [replacement] Give an optional replacement for this module (which
	 * needs to be already defined!)
	 */
	deprecate: function ( id, obj, replacement ) {
		var msg;
		if ( replacement ) {
			// add an alternative for this module, if any given
			msg = 'Use ' + replacement + ' instead.';
		}
		// register it as a deprecated one
		mw.log.deprecate( this._register, id, obj, msg );
	}
};

OO.mixinClass( ModuleLoader, OO.EventEmitter );

module.exports = ModuleLoader;
var ModuleLoader = require( './modules' ),
	moduleLoader = new ModuleLoader(),
	mfExtend = require( './mfExtend' ),
	util = require( './util' ),
	Browser = require( './Browser' ),
	cache = require( './cache' );

mw.mobileFrontend = moduleLoader;
mw.log.deprecate( moduleLoader, 'on', moduleLoader.on,
	'The global EventEmitter should not be used (T156186).' );

OO.mfExtend = mfExtend;

// I know there is a temptation to use moduleLoader here, but if you do resource-modules will fail
// as webpack might change the variable name. Using mw.mobileFrontend means that the variable
// will not be recast.
mw.mobileFrontend.define( 'mobile.startup/util', util );
mw.mobileFrontend.define( 'mobile.startup/Browser', Browser );
mw.mobileFrontend.define( 'mobile.startup/cache', cache );

// Expose the entry chunk through libraryTarget and library. This allows
// arbitrary file access via ResourceLoader like
// `mobileFrontend['mobile.startup'].moduleLoader.require('mobile.startup/LoadingOverlay')`.
module.exports = {
	moduleLoader: moduleLoader,
	util: util,
	Browser: Browser,
	cache: cache
};
/**
 * Extends a class with new methods and member properties.
 *
 * @param {Function} Child function
 * @param {Object|Function} ParentOrPrototype class to inherit from OR if no inheriting class a prototype to
 *  extend the class with,
 * @param {Object} prototype
 */
function mfExtend( Child, ParentOrPrototype, prototype ) {
	var key;
	if ( prototype ) {
		OO.inheritClass( Child, ParentOrPrototype );
	} else {
		OO.initClass( Child );
		prototype = ParentOrPrototype;
	}
	for ( key in prototype ) {
		Child.prototype[key] = prototype[key];
	}
}

module.exports = mfExtend;
/* This module defines several types of cache classes to use in other
 * modules.
 * The interface, that all types use, is kept synchronous driven by current
 * usage patterns, but will need to be revisited in case usage patterns
 * suggest we need asynchronous caches.
 */

/**
 * In memory cache implementation
 *
 * @class MemoryCache
 */
function MemoryCache() {
	this._cache = {};
}

/**
 * Retrieve a cached value from a key
 * @memberof MemoryCache
 * @instance
 * @param {string} key
 * @return {Mixed}
 */
MemoryCache.prototype.get = function ( key ) {
	return this._cache[ key ];
};

/**
 * Cache a value by key
 * @memberof MemoryCache
 * @instance
 * @param {string} key
 * @param {Mixed} value
 */
MemoryCache.prototype.set = function ( key, value ) {
	this._cache[ key ] = value;
};

/**
 * Null object cache implementation
 *
 * @class NoCache
 */
function NoCache() { }

/**
 * NoOp
 * @memberof NoCache
 * @instance
 */
NoCache.prototype.get = function () { };

/**
 * NoOp
 * @memberof NoCache
 * @instance
 */
NoCache.prototype.set = function () { };

module.exports = {
	MemoryCache: MemoryCache,
	NoCache: NoCache
};
