/* global $ */

/**
 * Utility library
 * @class util
 * @singleton
 */
module.exports = {
	/**
	 * Escape a string for use as a css selector
	 * @memberof util
	 * @instance
	 * @param {string} selector
	 * @return {string}
	 */
	escapeSelector: function ( selector ) {
		return $.escapeSelector( selector );
	},
	/**
	 * Wrapper class for the $.grep
	 * @memberof util
	 * @instance
	 * @return {jQuery.Deferred}
	 */
	grep: function () {
		return $.grep.apply( $, arguments );
	},
	/**
	 * Run method when document is ready.
	 * @memberof util
	 * @instance
	 * @param {Function} fn
	 * @return {jQuery.Object}
	 */
	docReady: function ( fn ) {
		return $( fn );
	},
	/**
	 * Wrapper class for the $.when
	 * @memberof util
	 * @instance
	 * @return {jQuery.Deferred}
	 */
	when: function () {
		return $.when.apply( $, arguments );
	},
	/**
	 * Wrapper class for the Deferred method
	 * @memberof util
	 * @instance
	 * @return {jQuery.Deferred}
	 */
	Deferred: function () {
		var d = $.Deferred(),
			warning = 'Use Promise compatible methods `then` and `catch` instead.';

		/* eslint-disable no-restricted-properties */
		mw.log.deprecate( d, 'fail', d.fail, warning );
		mw.log.deprecate( d, 'always', d.always, warning );
		mw.log.deprecate( d, 'done', d.done, warning );
		/* eslint-enable no-restricted-properties */
		return d;
	},
	/**
	 * Adds a class to the document
	 * @memberof util
	 * @instance
	 * @return {jQuery.Object} element representing the documentElement
	 */
	getDocument: function () {
		return $( document.documentElement );
	},
	/**
	 * Get the window object
	 * @memberof util
	 * @instance
	 * @return {jQuery.Object}
	 */
	getWindow: function () {
		return $( window );
	},
	/**
	 * Given some html, create new element(s).
	 * Unlike jQuery.parseHTML this will return a jQuery object
	 * not an array.
	 * @memberof util
	 * @instance
	 * @param {string} html
	 * @param {Element} [ctx] Document element to serve as the context
	 *  in which the HTML fragment will be created
	 * @return {jQuery.Object}
	 */
	parseHTML: function ( html, ctx ) {
		return $( $.parseHTML( html, ctx ) );
	},
	/**
	 * wrapper for jQuery util function to check if something is numeric
	 * @memberof util
	 * @instance
	 * @return {boolean}
	 */
	isNumeric: function () {
		return $.isNumeric.apply( $, arguments );
	},
	/**
	 * Wrapper for jQuery.extend method. In future this can be bound to Object.assign
	 * when support allows.
	 * @memberof util
	 * @instance
	 * @return {Object}
	 */
	extend: function () {
		return $.extend.apply( $, arguments );
	},
	/**
	 * Escape dots and colons in a hash, jQuery doesn't like them because they
	 * look like CSS classes and pseudoclasses. See
	 * http://bugs.jquery.com/ticket/5241
	 * http://stackoverflow.com/questions/350292/how-do-i-get-jquery-to-select-elements-with-a-period-in-their-id
	 * @memberof util
	 * @instance
	 * @param {string} hash A hash to escape
	 * @return {string}
	 */
	escapeHash: function ( hash ) {
		return hash.replace( /(:|\.)/g, '\\$1' );
	},

	/**
	 * Heuristic for determining whether an Event should be handled by
	 * MobileFrontend or allowed to bubble to the browser.
	 * @memberof util
	 * @instance
	 * @param {Event} ev
	 * @return {boolean} True if event is modified with control, alt, meta, or
	 *                   shift keys and should probably be handled by the
	 *                   browser.
	 *
	 * todo: move this function to a ClickUtil file once bundling and code
	 * splitting is supported.
	 */
	isModifiedEvent: function ( ev ) {
		return ev.altKey || ev.ctrlKey || ev.metaKey || ev.shiftKey;
	},

	/**
	 * Pipe event emitted by source through proxy. Subscribers to proxy will receive the event as
	 * though proxy was the originator.
	 *
	 * @param {OO.EventEmitter} src
	 * @param {OO.EventEmitter} proxy
	 * @param {string} event Event type to listen for.
	 * @param {any[]} [args] Arguments to pass to
	 *  subscribers, will be prepended to emitted arguments.
	 * @return {OO.EventEmitter} The source.
	 */
	repeatEvent: function ( src, proxy, event, args ) {
		return src.on( event, function ( args ) { return proxy.emit( event, args ); }, args );
	}
};
/**
 * Extends a class with new methods and member properties.
 *
 * @param {Function} Child function
 * @param {Object|Function} ParentOrPrototype class to inherit from
 *  OR if no inheriting class a prototype to extend the class with
 * @param {Object} [prototype]
 */
function mfExtend( Child, ParentOrPrototype, prototype ) {
	var key;
	if ( prototype ) {
		OO.inheritClass( Child, ParentOrPrototype );
	} else {
		OO.initClass( Child );
		prototype = ParentOrPrototype;
	}
	for ( key in prototype ) {
		Child.prototype[key] = prototype[key];
	}
}

module.exports = mfExtend;
/* global $ */
var util = require( './util' ),
	mfExtend = require( './mfExtend' ),
	// Cached regex to split keys for `delegate`.
	delegateEventSplitter = /^(\S+)\s*(.*)$/,
	idCounter = 0;

/**
 * Generate a unique integer id (unique within the entire client session).
 * Useful for temporary DOM ids.
 * @param {string} prefix Prefix to be used when generating the id.
 * @return {string}
 */
function uniqueId( prefix ) {
	var id = ( ++idCounter ).toString();
	return prefix ? prefix + id : id;
}

/**
 * Should be extended using extend().
 *
 * When options contains el property, this.$el in the constructed object
 * will be set to the corresponding jQuery object. Otherwise, this.$el
 * will be an empty div.
 *
 * When extended using extend(), if the extended prototype contains
 * template property, this.$el will be filled with rendered template (with
 * options parameter used as template data).
 *
 * template property can be a string which will be passed to mw.template.compile()
 * or an object that has a render() function which accepts an object with
 * template data as its argument (similarly to an object created by
 * mw.template.compile()).
 *
 * You can also define a defaults property which should be an object
 * containing default values for the template (if they're not present in
 * the options parameter).
 *
 * If this.$el is not a jQuery object bound to existing DOM element, the
 * view can be attached to an element using appendTo(), prependTo(),
 * insertBefore(), insertAfter() proxy functions.
 *
 * append(), prepend(), before(), after() can be used to modify $el. on()
 * can be used to bind events.
 *
 * You can also use declarative DOM events binding by specifying an `events`
 * map on the class. The keys will be 'event selector' and the value can be
 * either the name of a method to call, or a function. All methods and
 * functions will be executed on the context of the View.
 *
 * Inspired from Backbone.js
 * https://github.com/jashkenas/backbone/blob/master/backbone.js#L1128
 *
 * Example:
 * ```js
 *     var MyComponent = View.extend( {
 *       events: {
 *	       'mousedown .title': 'edit',
 *	       'click .button': 'save',
 *	       'click .open': function(e) { ... }
 *       },
 *       edit: function ( ev ) {
 *         //...
 *       },
 *       save: function ( ev ) {
 *         //...
 *       }
 *     } );
 * ```
 *
 * Example:
 * ```js
 *     var View, section;
 *     function Section( options ) {
 *       View.call( this, options );
 *     }
 *     View = require( './View' );
 *     require( './mfExtend' )( Section, View, {
 *       template: mw.template.compile( "&lt;h2&gt;{{title}}&lt;/h2&gt;" ),
 *     } );
 *     section = new Section( { title: 'Test', text: 'Test section body' } );
 *     section.appendTo( 'body' );
 * ```
 *
 * @class View
 * @mixins OO.EventEmitter
 */
function View() {
	this.initialize.apply( this, arguments );
}
OO.mixinClass( View, OO.EventEmitter );
mfExtend( View, {
	/**
	 * A css class to apply to the containing element of the View.
	 * @memberof View
	 * @instance
	 * @property {string} className
	 */
	className: undefined,
	/**
	 * Name of tag that contains the rendered template
	 * @memberof View
	 * @instance
	 * @property {string} tagName
	 */
	tagName: 'div',
	/**
	 * Tells the View to ignore tagName and className when constructing the element
	 * and to rely solely on the template
	 * @memberof View
	 * @instance
	 * @property {boolean} isTemplateMode
	 */
	isTemplateMode: false,

	/**
	 * Whether border box box sizing model should be used
	 * @memberof View
	 * @instance
	 * @property {boolean} isBorderBox
	 */
	isBorderBox: true,
	/**
	 * @memberof View
	 * @instance
	 * @property {Mixed}
	 * Specifies the template used in render(). Object|string|HoganTemplate
	 */
	template: undefined,

	/**
	 * Specifies partials (sub-templates) for the main template. Example:
	 *
	 *     @example
	 *     // example content for the "some" template (sub-template will be
	 *     // inserted where {{>content}} is):
	 *     // <h1>Heading</h1>
	 *     // {{>content}}
	 *
	 *     oo.mfExtend( SomeView, View, {
	 *       template: M.template.get( 'some.hogan' ),
	 *       templatePartials: { content: M.template.get( 'sub.hogan' ) }
	 *     }
	 *
	 * @memberof View
	 * @instance
	 * @property {Object}
	 */
	templatePartials: {},

	/**
	 * A set of default options that are merged with options passed into the initialize
	 * function.
	 * @memberof View
	 * @instance
	 * @property {Object} defaults Default options hash.
	 * @property {jQuery.Object|string} [defaults.el] jQuery selector to use for rendering.
	 * @property {boolean} [defaults.skipTemplateRender] Whether to enhance views already in
	 * DOM. When enabled, the template is disabled so that it is not rendered in the DOM.
	 * Use in conjunction with View::defaults.$el to associate the View with an existing
	 * already rendered element in the DOM.
	 */
	defaults: {},

	/**
	 * Default events map
	 * @memberof View
	 * @instance
	 */
	events: null,

	/**
	 * Run once during construction to set up the View
	 * @memberof View
	 * @instance
	 * @param {Object} options Object passed to the constructor.
	 */
	initialize: function ( options ) {
		var self = this;

		OO.EventEmitter.call( this );
		options = util.extend( {}, this.defaults, options );
		this.options = options;
		// Assign a unique id for dom events binding/unbinding
		this.cid = uniqueId( 'view' );

		// TODO: if template compilation is too slow, don't compile them on a
		// per object basis, but don't worry about it now (maybe add cache to
		// M.template.compile())
		if ( typeof this.template === 'string' ) {
			this.template = mw.template.compile( this.template );
		}

		if ( options.el ) {
			// Note the element may not be in the document so must use global jQuery here
			this.$el = $( options.el );
		} else {
			this.$el = this.parseHTML( '<' + this.tagName + '>' );
		}

		// Make sure the element is ready to be manipulated
		if ( this.$el.length ) {
			this._postInitialize();
		} else {
			util.docReady( function () {
				// Note the element may not be in the document so must use global jQuery here
				self.$el = $( options.el );
				self._postInitialize();
			} );
		}
	},

	/**
	 * Called when this.$el is ready.
	 * @memberof View
	 * @instance
	 * @private
	 */
	_postInitialize: function () {
		this.$el.addClass( this.className );
		if ( this.isBorderBox ) {
			// FIXME: Merge with className property (?)
			this.$el.addClass( 'view-border-box' );
		}
		this.render( this.options );
	},

	/**
	 * Function called before the view is rendered. Can be redefined in
	 * objects that extend View.
	 * @memberof View
	 * @instance
	 */
	preRender: function () {},

	/**
	 * Function called after the view is rendered. Can be redefined in
	 * objects that extend View.
	 *
	 * @memberof View
	 * @instance
	 */
	postRender: function () {},

	// eslint-disable-next-line valid-jsdoc
	/**
	 * Fill this.$el with template rendered using data if template is set.
	 *
	 * @memberof View
	 * @instance
	 * @param {Object} data Template data. Will be merged into the view's
	 * options
	 * @chainable
	 */
	render: function ( data ) {
		var html;
		util.extend( this.options, data );
		this.preRender();
		this.undelegateEvents();
		if ( this.template && !this.options.skipTemplateRender ) {
			html = this.template.render( this.options, this.templatePartials );
			if ( this.isTemplateMode ) {
				this.$el = $( html );
			} else {
				this.$el.html( html );
			}
		}
		this.postRender();
		this.delegateEvents();
		return this;
	},

	/**
	 * Wraps this.$el.find, so that you can search for elements in the view's
	 * ($el's) scope.
	 *
	 * @memberof View
	 * @instance
	 * @param {string} query A jQuery CSS selector.
	 * @return {JQuery.Object} jQuery object containing results of the search.
	 */
	$: function ( query ) {
		return this.$el.find( query );
	},

	/**
	 * Set callbacks, where `this.events` is a hash of
	 *
	 * {"event selector": "callback"}
	 *
	 * {
	 *	'mousedown .title': 'edit',
	 *	'click .button': 'save',
	 *	'click .open': function(e) { ... }
	 * }
	 *
	 * pairs. Callbacks will be bound to the view, with `this` set properly.
	 * Uses event delegation for efficiency.
	 * Omitting the selector binds the event to `this.el`.
	 *
	 * @memberof View
	 * @instance
	 * @param {Object} events Optionally set this events instead of the ones on this.
	 */
	delegateEvents: function ( events ) {
		var match, key, method;
		// Take either the events parameter or the this.events to process
		events = events || this.events;
		if ( events ) {
			// Remove current events before re-binding them
			this.undelegateEvents();
			for ( key in events ) {
				method = events[ key ];
				// If the method is a string name of this.method, get it
				if ( typeof method !== 'function' ) {
					method = this[ events[ key ] ];
				}
				if ( method ) {
					// Extract event and selector from the key
					match = key.match( delegateEventSplitter );
					this.delegate( match[ 1 ], match[ 2 ], method.bind( this ) );
				}
			}
		}
	},

	/**
	 * Add a single event listener to the view's element (or a child element
	 * using `selector`). This only works for delegate-able events: not `focus`,
	 * `blur`, and not `change`, `submit`, and `reset` in Internet Explorer.
	 *
	 * @memberof View
	 * @instance
	 * @param {string} eventName
	 * @param {string} selector
	 * @param {Function} listener
	 */
	delegate: function ( eventName, selector, listener ) {
		this.$el.on( eventName + '.delegateEvents' + this.cid, selector,
			listener );
	},

	/**
	 * Clears all callbacks previously bound to the view by `delegateEvents`.
	 * You usually don't need to use this, but may wish to if you have multiple
	 * views attached to the same DOM element.
	 * @memberof View
	 * @instance
	 */
	undelegateEvents: function () {
		if ( this.$el ) {
			this.$el.off( '.delegateEvents' + this.cid );
		}
	},

	/**
	 * A finer-grained `undelegateEvents` for removing a single delegated event.
	 * `selector` and `listener` are both optional.
	 *
	 * @memberof View
	 * @instance
	 * @param {string} eventName
	 * @param {string} selector
	 * @param {Function} listener
	 */
	undelegate: function ( eventName, selector, listener ) {
		this.$el.off( eventName + '.delegateEvents' + this.cid, selector,
			listener );
	},

	/**
	 * See parseHTML method of util singleton
	 *
	 * @memberof View
	 * @instance
	 */
	parseHTML: util.parseHTML
} );

/**
 * @memberof View
 * @instance
 * @func append
 * @param {...(string|Node|Node[]|JQuery)} contents
 * @return {this}
 */

/**
 * @memberof View
 * @instance
 * @func append
 * @param {function(number, string): string|Node|Node[]|JQuery} contents
 * @return {this}
 */

/**
 * @memberof View
 * @instance
 * @func prepend
 * @param {...(string|Node|Node[]|JQuery)} contents
 * @return {this}
 */

/**
 * @memberof View
 * @instance
 * @func prepend
 * @param {function(number, string): string|Node|Node[]|JQuery} contents
 * @return {this}
 */

/**
 * @memberof View
 * @instance
 * @func appendTo
 * @param {string|Node|Node[]|JQuery} target
 * @return {this}
 */

/**
 * @memberof View
 * @instance
 * @func prependTo
 * @param {string|Node|Node[]|JQuery} target
 * @return {this}
 */

/**
 * @memberof View
 * @instance
 * @func after
 * @param {...(string|Node|Node[]|JQuery)} contents
 * @return {this}
 */

/**
 * @memberof View
 * @instance
 * @func after
 * @param {function(number, string): string|Node|Node[]|JQuery} contents
 * @return {this}
 */

/**
 * @memberof View
 * @instance
 * @func before
 * @param {...(string|Node|Node[]|JQuery)} contents
 * @return {this}
 */

/**
 * @memberof View
 * @instance
 * @func before
 * @param {function(number, string): string|Node|Node[]|JQuery} contents
 * @return {this}
 */

/**
 * @memberof View
 * @instance
 * @func insertAfter
 * @param {string|Node|Node[]|JQuery} target
 * @return {this}
 */

/**
 * @memberof View
 * @instance
 * @func insertBefore
 * @param {string|Node|Node[]|JQuery} target
 * @return {this}
 */

/**
 * @memberof View
 * @instance
 * @func remove
 * @param {string} [selector]
 * @return {this}
 */

/**
 * @memberof View
 * @instance
 * @func detach
 * @param {string} [selector]
 * @return {this}
 */

[
	'append',
	'prepend',
	'appendTo',
	'prependTo',
	'after',
	'before',
	'insertAfter',
	'insertBefore',
	'remove',
	'detach'
].forEach( function ( prop ) {
	View.prototype[prop] = function () {
		this.$el[prop].apply( this.$el, arguments );
		return this;
	};
} );

module.exports = View;
var g;

// This works in non-strict mode
g = (function() {
	return this;
})();

try {
	// This works if eval is allowed (see CSP)
	g = g || Function("return this")() || (1, eval)("this");
} catch (e) {
	// This works if the window reference is available
	if (typeof window === "object") g = window;
}

// g can still be undefined, but nothing to do about it...
// We return undefined, instead of nothing here, so it's
// easier to handle this case. if(!global) { ...}

module.exports = g;
(function() { module.exports = this["sinon"]; }());
var units = [ 'seconds', 'minutes', 'hours', 'days', 'months', 'years' ],
	util = require( './util' ),
	limits = [ 1, 60, 3600, 86400, 2592000, 31536000 ];

/** @class time */

/**
 * Calculate the correct unit of timestamp
 * @memberof time
 * @instance
 * @param {number} timestampDelta
 * @return {{value: number, unit: string}}
 */
function timeAgo( timestampDelta ) {
	var i = 0;
	while ( i < limits.length && timestampDelta > limits[i + 1] ) {
		++i;
	}
	return {
		value: Math.round( timestampDelta / limits[i] ),
		unit: units[i]
	};
}

/**
 * Calculate the correct unit of timestamp delta
 * @memberof time
 * @instance
 * @param {number} timestamp
 * @return {{value: number, unit: string}}
 */
function getTimeAgoDelta( timestamp ) {
	var currentTimestamp = Math.round( new Date().getTime() / 1000 );

	return timeAgo( currentTimestamp - timestamp );
}

/**
 * Whether timestamp delta is less than a day old
 * @memberof time
 * @instance
 * @param {{value: number, unit: string}} delta Object of timestamp and its label
 * @return {boolean}
 */
function isRecent( delta ) {
	return [ 'seconds', 'minutes', 'hours' ].indexOf( delta.unit ) > -1;
}

/**
 * Is delta less than 10 seconds?
 * @memberof time
 * @instance
 * @param {{value: number, unit: string}} delta Object of timestamp and its label
 * @return {boolean}
 */
function isNow( delta ) {
	return delta.unit === 'seconds' && delta.value < 10;
}

/**
 * Return a message relating to the last modified relative time.
 * @memberof time
 * @instance
 * @param {number} ts timestamp
 * @param {string} username of the last user to modify the page
 * @param {string} [gender] of the last user to modify the page
 * @param {string} [historyUrl] url to the history page for the message, if omitted
 *  returns plain text string rather than html
 * @return {string}
 */
function getLastModifiedMessage( ts, username, gender, historyUrl ) {
	var delta, html,
		keys = {
			seconds: 'mobile-frontend-last-modified-with-user-seconds',
			minutes: 'mobile-frontend-last-modified-with-user-minutes',
			hours: 'mobile-frontend-last-modified-with-user-hours',
			days: 'mobile-frontend-last-modified-with-user-days',
			months: 'mobile-frontend-last-modified-with-user-months',
			years: 'mobile-frontend-last-modified-with-user-years'
		},
		args = [];

	gender = gender || 'unknown';

	delta = getTimeAgoDelta( ts );
	if ( isNow( delta ) ) {
		args.push( 'mobile-frontend-last-modified-with-user-just-now', gender, username );
	} else {
		args.push( keys[ delta.unit ], gender, username,
			mw.language.convertNumber( delta.value )
		);
	}

	args.push(
		historyUrl || '#',
		// Abuse PLURAL support to determine if the user is anonymous or not
		mw.language.convertNumber( username ? 1 : 0 ),
		// Our abuse of PLURAL support means we have to pass the relative URL
		// rather than construct it from a wikilink
		username ? mw.util.getUrl( 'User:' + username ) : ''
	);
	html = mw.message.apply( this, args ).parse();
	if ( historyUrl ) {
		return html;
	} else {
		return util.parseHTML( '<div>' ).html( html ).text();
	}
}

/**
 * Return a message relating to the registration date of the user
 * @memberof time
 * @instance
 * @param {string} ts timestamp
 * @param {string} [gender] of the last user editing this page
 * @return {string}
 */
function getRegistrationMessage( ts, gender ) {
	var delta, html,
		keys = {
			seconds: 'mobile-frontend-joined-seconds',
			minutes: 'mobile-frontend-joined-minutes',
			hours: 'mobile-frontend-joined-hours',
			days: 'mobile-frontend-joined-days',
			months: 'mobile-frontend-joined-months',
			years: 'mobile-frontend-joined-years'
		},
		args = [];

	gender = gender || 'unknown';

	delta = getTimeAgoDelta( parseInt( ts, 10 ) );
	if ( isNow( delta ) ) {
		args.push( 'mobile-frontend-joined-just-now', gender );
	} else {
		args.push( keys[ delta.unit ], gender, mw.language.convertNumber( delta.value ) );
	}
	html = mw.message.apply( this, args ).parse();
	return html;
}

module.exports = {
	getLastModifiedMessage: getLastModifiedMessage,
	getRegistrationMessage: getRegistrationMessage,
	timeAgo: timeAgo,
	getTimeAgoDelta: getTimeAgoDelta,
	isNow: isNow,
	isRecent: isRecent
};
var
	util = require( './util' ),
	browser;

/**
 * Memoize a class method. Caches the result of the method based on the
 * arguments. Instances do not share a cache.
 * @param {Function} method Method to be memoized
 * @return {Function}
 */
function memoize( method ) {
	/**
	 * Memoized version of the method
	 * @return {Function}
	 */
	var memoized = function () {
		var cache = this[ '__cache' + memoized.cacheId ] ||
			( this[ '__cache' + memoized.cacheId ] = {} ),
			key = [].join.call( arguments, '|' );
		if ( Object.prototype.hasOwnProperty.call( cache, key ) ) {
			return cache[ key ];
		}
		return ( cache[ key ] = method.apply( this, arguments ) );
	};
	memoized.cacheId = Date.now().toString() + Math.random().toString();
	return memoized;
