 *  Unless required by applicable law or agreed to in writing, software
 *  distributed under the License is distributed on an "AS IS" BASIS,
 *  WITHOUT WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied.
 *  See the License for the specific language governing permissions and
 *  limitations under the License.
 */

var Hogan = {};

(function (Hogan, useArrayBuffer) {
  Hogan.Template = function (renderFunc, text, compiler, options) {
    this.r = renderFunc || this.r;
    this.c = compiler;
    this.options = options;
    this.text = text || '';
    this.buf = (useArrayBuffer) ? [] : '';
  }

  Hogan.Template.prototype = {
    // render: replaced by generated code.
    r: function (context, partials, indent) { return ''; },

    // variable escaping
    v: hoganEscape,

    // triple stache
    t: coerceToString,

    render: function render(context, partials, indent) {
      return this.ri([context], partials || {}, indent);
    },

    // render internal -- a hook for overrides that catches partials too
    ri: function (context, partials, indent) {
      return this.r(context, partials, indent);
    },

    // tries to find a partial in the curent scope and render it
    rp: function(name, context, partials, indent) {
      var partial = partials[name];

      if (!partial) {
        return '';
      }

      if (this.c && typeof partial == 'string') {
        partial = this.c.compile(partial, this.options);
      }

      return partial.ri(context, partials, indent);
    },

    // render a section
    rs: function(context, partials, section) {
      var tail = context[context.length - 1];

      if (!isArray(tail)) {
        section(context, partials, this);
        return;
      }

      for (var i = 0; i < tail.length; i++) {
        context.push(tail[i]);
        section(context, partials, this);
        context.pop();
      }
    },

    // maybe start a section
    s: function(val, ctx, partials, inverted, start, end, tags) {
      var pass;

      if (isArray(val) && val.length === 0) {
        return false;
      }

      if (typeof val == 'function') {
        val = this.ls(val, ctx, partials, inverted, start, end, tags);
      }

      pass = (val === '') || !!val;

      if (!inverted && pass && ctx) {
        ctx.push((typeof val == 'object') ? val : ctx[ctx.length - 1]);
      }

      return pass;
    },

    // find values with dotted names
    d: function(key, ctx, partials, returnFound) {
      var names = key.split('.'),
          val = this.f(names[0], ctx, partials, returnFound),
          cx = null;

      if (key === '.' && isArray(ctx[ctx.length - 2])) {
        return ctx[ctx.length - 1];
      }

      for (var i = 1; i < names.length; i++) {
        if (val && typeof val == 'object' && names[i] in val) {
          cx = val;
          val = val[names[i]];
        } else {
          val = '';
        }
      }

      if (returnFound && !val) {
        return false;
      }

      if (!returnFound && typeof val == 'function') {
        ctx.push(cx);
        val = this.lv(val, ctx, partials);
        ctx.pop();
      }

      return val;
    },

    // find values with normal names
    f: function(key, ctx, partials, returnFound) {
      var val = false,
          v = null,
          found = false;

      for (var i = ctx.length - 1; i >= 0; i--) {
        v = ctx[i];
        if (v && typeof v == 'object' && key in v) {
          val = v[key];
          found = true;
          break;
        }
      }

      if (!found) {
        return (returnFound) ? false : "";
      }

      if (!returnFound && typeof val == 'function') {
        val = this.lv(val, ctx, partials);
      }

      return val;
    },

    // higher order templates
    ho: function(val, cx, partials, text, tags) {
      var compiler = this.c;
      var options = this.options;
      options.delimiters = tags;
      var text = val.call(cx, text);
      text = (text == null) ? String(text) : text.toString();
      this.b(compiler.compile(text, options).render(cx, partials));
      return false;
    },

    // template result buffering
    b: (useArrayBuffer) ? function(s) { this.buf.push(s); } :
                          function(s) { this.buf += s; },
    fl: (useArrayBuffer) ? function() { var r = this.buf.join(''); this.buf = []; return r; } :
                           function() { var r = this.buf; this.buf = ''; return r; },

    // lambda replace section
    ls: function(val, ctx, partials, inverted, start, end, tags) {
      var cx = ctx[ctx.length - 1],
          t = null;

      if (!inverted && this.c && val.length > 0) {
        return this.ho(val, cx, partials, this.text.substring(start, end), tags);
      }

      t = val.call(cx);

      if (typeof t == 'function') {
        if (inverted) {
          return true;
        } else if (this.c) {
          return this.ho(t, cx, partials, this.text.substring(start, end), tags);
        }
      }

      return t;
    },

    // lambda replace variable
    lv: function(val, ctx, partials) {
      var cx = ctx[ctx.length - 1];
      var result = val.call(cx);

      if (typeof result == 'function') {
        result = coerceToString(result.call(cx));
        if (this.c && ~result.indexOf("{\u007B")) {
          return this.c.compile(result, this.options).render(cx, partials);
        }
      }

      return coerceToString(result);
    }

  };

  var rAmp = /&/g,
      rLt = /</g,
      rGt = />/g,
      rApos =/\'/g,
      rQuot = /\"/g,
      hChars =/[&<>\"\']/;


  function coerceToString(val) {
    return String((val === null || val === undefined) ? '' : val);
  }

  function hoganEscape(str) {
    str = coerceToString(str);
    return hChars.test(str) ?
      str
        .replace(rAmp,'&amp;')
        .replace(rLt,'&lt;')
        .replace(rGt,'&gt;')
        .replace(rApos,'&#39;')
        .replace(rQuot, '&quot;') :
      str;
  }

  var isArray = Array.isArray || function(a) {
    return Object.prototype.toString.call(a) === '[object Array]';
  };

})(typeof exports !== 'undefined' ? exports : Hogan);

var g;

// This works in non-strict mode
g = (function() {
	return this;
})();

try {
	// This works if eval is allowed (see CSP)
	g = g || Function("return this")() || (1, eval)("this");
} catch (e) {
	// This works if the window reference is available
	if (typeof window === "object") g = window;
}

// g can still be undefined, but nothing to do about it...
// We return undefined, instead of nothing here, so it's
// easier to handle this case. if(!global) { ...}

module.exports = g;
var
	prototype,
	actionParams = require( '../mobile.startup/actionParams' ),
	util = require( '../mobile.startup/util' ),
	SearchGateway = require( '../mobile.startup/search/SearchGateway' );

/**
 * Api for CategoryOverlay
 * @class CategoryGateway
 * @extends SearchGateway
 */
function CategoryGateway() {
	CategoryGateway.parent.apply( this, arguments );
}
prototype = {
	/**
	 * @memberof CategoryGateway
	 * @instance
	 */
	continueParams: {},
	/**
	 * @memberof CategoryGateway
	 * @instance
	 */
	canContinue: true,
	/**
	 * @inheritdoc
	 * @memberof CategoryGateway
	 * @instance
	 */
	searchNamespace: 14,
	/**
	 * Saves the categories passed to this function to the page
	 * @memberof CategoryGateway
	 * @instance
	 * @param {string} title Title of the current page (to add the categories to)
	 * @param {string} categories List of Categories to add
	 * @return {jQuery.Deferred}
	 */
	save: function ( title, categories ) {
		return this.api.postWithToken( 'csrf', {
			action: 'edit',
			title: title,
			appendtext: categories,
			summary: mw.msg( 'mobile-frontend-categories-summary' )
		} );
	},
	/**
	 * Returns the categories the title belongs to.
	 * @memberof CategoryGateway
	 * @instance
	 * @param {string} title Title of the current page (to add the categories to)
	 * @return {jQuery.Deferred|boolean} False, if no further continuation is possible,
	 *                                   jQuery.Deferred otherwise.
	 */
	getCategories: function ( title ) {
		var self = this, params;

		if ( this.canContinue === false ) {
			return false;
		}

		params = util.extend( {}, {
			prop: 'categories',
			titles: title,
			clprop: 'hidden',
			cllimit: 50
		}, this.continueParams );
		return this.api.get( actionParams( params ) ).then( function ( data ) {
			if ( data.continue !== undefined ) {
				self.continueParams = data.continue;
			} else {
				self.canContinue = false;
			}

			return data;
		} );
	}
};

OO.inheritClass( CategoryGateway, SearchGateway );
util.extend( CategoryGateway.prototype, prototype );

module.exports = CategoryGateway;
var Button = require( '../mobile.startup/Button' ),
	util = require( '../mobile.startup/util' ),
	mfExtend = require( '../mobile.startup/mfExtend' ),
	Overlay = require( '../mobile.startup/Overlay' );

/**
 * Overlay that shows a message about abuse.
 * This overlay is rendered when the error code from the API
 * is related to the abusefilter extension.
 * @class AbuseFilterOverlay
 * @extends Overlay
 * @param {Object} props
 */
function AbuseFilterOverlay( props ) {
	Overlay.call( this,
		util.extend( {
			className: 'overlay abusefilter-overlay'
		}, props )
	);
}

mfExtend( AbuseFilterOverlay, Overlay, {
	/**
	 * @memberof AbuseFilterOverlay
	 * @instance
	 * @mixes Overlay#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {Object} defaults.confirmButton options for a confirm Button
	 */
	defaults: util.extend( {}, Overlay.prototype.defaults, {
		confirmButton: new Button( {
			additionalClassNames: 'cancel',
			progressive: true
		} ).options
	} ),
	/**
	 * @memberof AbuseFilterOverlay
	 * @instance
	 */
	templatePartials: util.extend( {}, Overlay.prototype.templatePartials, {
		button: Button.prototype.template,
		content: mw.template.get( 'mobile.editor.overlay', 'AbuseFilterOverlay.hogan' )
	} ),
	/**
	 * @inheritdoc
	 * @memberof AbuseFilterOverlay
	 * @instance
	 */
	postRender: function () {
		Overlay.prototype.postRender.apply( this );
		// make links open in separate tabs
		this.$( 'a' ).attr( 'target', '_blank' );
	}
} );

module.exports = AbuseFilterOverlay;
var util = require( '../mobile.startup/util' ),
	View = require( '../mobile.startup/View' ),
	mfExtend = require( '../mobile.startup/mfExtend' ),
	AbuseFilterOverlay = require( './AbuseFilterOverlay' );

/**
 * Panel that shows an error message related to the abusefilter extension.
 * @class AbuseFilterPanel
 * @extends View
 * @uses AbuseFilterOverlay
 *
 * @param {Object} options Configuration options
 * FIXME: should extend Panel not View. Or the name should be changed to something meaningful.
 */
function AbuseFilterPanel( options ) {
	this.isDisallowed = false;
	this.overlayManager = options.overlayManager;
	View.call( this,
		util.extend( {
			className: 'panel hidden'
		}, options )
	);
}

mfExtend( AbuseFilterPanel, View, {
	/**
	 * @memberof AbuseFilterPanel
	 * @instance
	 * @mixes View#defaults
	 * @property {Object} defaults Default options hash.
	 * @property {string} defaults.readMoreMsg A caption for the button
	 * allowing the user to read more about the problems with their edit.
	 * @property {OverlayManager} defaults.overlayManager instance
	 */
	defaults: {
		readMoreMsg: mw.msg( 'mobile-frontend-editor-abusefilter-read-more' )
	},
	/**
	 * @memberof AbuseFilterPanel
	 * @instance
	 */
	template: mw.template.get( 'mobile.editor.overlay', 'AbuseFilterPanel.hogan' ),
	/**
	 * Show the panel. Create a route to show AbuseFilterOverlay with message.
	 * @memberof AbuseFilterPanel
	 * @instance
	 * @param {string} type The type of alert, e.g. 'warning' or 'disallow'
	 * @param {string} message Message to show in the AbuseFilter overlay
	 */
	show: function ( type, message ) {
		var msg;

		// OverlayManager will replace previous instance of the route if present
		this.overlayManager.add( /^\/abusefilter$/, function () {
			return new AbuseFilterOverlay( {
				message: message
			} );
		} );

		if ( type === 'warning' ) {
			msg = mw.msg( 'mobile-frontend-editor-abusefilter-warning' );
		} else if ( type === 'disallow' ) {
			msg = mw.msg( 'mobile-frontend-editor-abusefilter-disallow' );
			this.isDisallowed = true;
		}

		this.$( '.message p' ).text( msg );
		this.$el.removeClass( 'hidden' );
	},

	/**
	 * Hide the panel.
	 * @memberof AbuseFilterPanel
	 * @instance
	 */
	hide: function () {
		this.$el.addClass( 'hidden' );
	}
} );

module.exports = AbuseFilterPanel;
'use strict';
var Drawer = require( '../mobile.startup/Drawer' ),
