	// trigger event to show events didn't get lost
	view.$( 'span' ).trigger( 'click' );
	assert.strictEqual( view.$el.text(), 'hello world', 'event was called' );
	assert.strictEqual( view.$( 'span' ).length, 0, 'span disappeared' );
} );

